css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("php.net") {

  h1, h2, a, a:hover, a:visited {
    color: #8cd0d3;
  }

  body, html {
    background-color: black !important;
    color: #dcdccc !important;
  }

  #content, div.text {
    background-color: #2a2a2a !important;
    color: #dcdccc !important;
  }

  body > div > div > ul > li > a, #layout_2, #leftbar, div.head {
    background-color: #1a1a1a !important;
    color: #dcdccc !important;
  }

  div.note {
    background-color: #0a0a0a !important;
    color: #dcdccc !important;
  }

  div.manualnavbar {
    background-color: #1a1a1a !important;
  }

  table.EXAMPLE {
    background-color: #3a3a3a !important;
  }
  div.example > table, div.phpcode {
    margin-left: 5px;
    background-color: #2a2a2a !important;
  }

  table.mirrors td.caret {
    background-color: black !important;
  }
/* PHP source code highlighting ------------------------------------------- */
div.phpcode span.html {
	color: #dcdccc;
	background-color: transparent;
}
div.phpcode span.comment {
	color: #7f9f7f;
	background-color: transparent;
}
div.phpcode span.default {
	color: #dcdccc;
	background-color: transparent;
}
div.phpcode span.keyword {
	color: #f0dfaf;
	background-color: transparent;
}
div.phpcode span.string {
	color: #cc9393;
	background-color: transparent;
}

div.tip, p.tip {
	border: 1px solid #000000;
	color: #7faf8f;
	background-color: #3f3f3f;
	padding: 8px;
}


}
