css:
@-moz-document domain("cnblogs.com") {
    @font-face {
      font-family: 'Roboto';
      font-style: normal;
      font-weight: 300;
      src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v16/Hgo13k-tfSpn0qi1SFdUfVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto';
      font-style: normal;
      font-weight: 400;
      src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v16/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto';
      font-style: normal;
      font-weight: 700;
      src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v16/d-6IYplOFocCacKzxwXSOFtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto';
      font-style: italic;
      font-weight: 300;
      src: local('Roboto Light Italic'), local('Roboto-LightItalic'), url(https://fonts.gstatic.com/s/roboto/v16/7m8l7TlFO-S3VkhHuR0at44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto';
      font-style: italic;
      font-weight: 400;
      src: local('Roboto Italic'), local('Roboto-Italic'), url(https://fonts.gstatic.com/s/roboto/v16/vPcynSL0qHq_6dX7lKVByfesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto';
      font-style: italic;
      font-weight: 700;
      src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(https://fonts.gstatic.com/s/roboto/v16/t6Nd4cfPRhZP44Q5QAjcC44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
    }

    /* Roboto Mono: Light, Regular, Bold */
    @font-face {
      font-family: 'Roboto Mono';
      font-style: normal;
      font-weight: 300;
      src: local('Roboto Mono Light'), local('RobotoMono-Light'), url(https://fonts.gstatic.com/s/robotomono/v4/N4duVc9C58uwPiY8_59Fz0o2lTMeWA_kmIyWrkNCwPc.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto Mono';
      font-style: normal;
      font-weight: 400;
      src: local('Roboto Mono'), local('RobotoMono-Regular'), url(https://fonts.gstatic.com/s/robotomono/v4/hMqPNLsu_dywMa4C_DEpY44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto Mono';
      font-style: normal;
      font-weight: 700;
      src: local('Roboto Mono Bold'), local('RobotoMono-Bold'), url(https://fonts.gstatic.com/s/robotomono/v4/N4duVc9C58uwPiY8_59Fz4lIZu-HDpmDIZMigmsroc4.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto Mono';
      font-style: italic;
      font-weight: 300;
      src: local('Roboto Mono Light Italic'), local('RobotoMono-LightItalic'), url(https://fonts.gstatic.com/s/robotomono/v4/1OsMuiiO6FCF2x67vzDKA2JNL1kWLlAYCVnltOJOPdY.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto Mono';
      font-style: italic;
      font-weight: 400;
      src: local('Roboto Mono Italic'), local('RobotoMono-Italic'), url(https://fonts.gstatic.com/s/robotomono/v4/mE0EPT_93c7f86_WQexR3Mu2Q0OS-KeTAWjgkS85mDg.woff2) format('woff2');
    }
    @font-face {
      font-family: 'Roboto Mono';
      font-style: italic;
      font-weight: 700;
      src: local('Roboto Mono Bold Italic'), local('RobotoMono-BoldItalic'), url(https://fonts.gstatic.com/s/robotomono/v4/1OsMuiiO6FCF2x67vzDKA49ObOXPY1wUIXqKtDjSdsY.woff2) format('woff2');
    }

	div#header, div#sideBar, div#sideToolbar, div#blog_post_info_block, div#comment_form, div#footer, 
	div.postDesc {
	    display: none !important;
	}

	div#main {
	    width:	90% !important;
	}

	div#mainContent {
	    width:	100% !important;
	}

	/* text */
	p, font[style="font-size: 10.5pt"], span[style="font-size: 10.5pt"] {
	    font-family:	'Roboto', 'Helvetica Nenu', Helvetica, Arial, Verdana, 'Microsoft YaHei' !important;
	    font-size:		18px !important;
        font-weight:    400 !important;
      	line-height:	1.5em !important;
	}

	/* formatted code */
	font[style="font-size: 9pt"], span[style="font-size: 9pt"] {
	    font-family:	'Roboto Mono', Inconsolata, 'Courier New', 'Microsoft YaHei' !important;
	    font-size:		16px !important;
        font-weight:    400 !important;
      	line-height:	1.5em !important;
	}

	font[face="宋体"], span[style="font-family: 宋体;"], font[face="SimSun"], span[style="font-family: SimSun;"], 
  	span[style="font-family: 'Century Gothic','sans-serif';"] {
	    font-family:	Roboto, 'Helvetica Nenu', Helvetica, Arial, Verdana, 'Microsoft YaHei' !important;
        font-weight:    400 !important;
	}

	body {
	    background-color:	#FFF !important;
	}

	div#topics {
	    box-shadow:		none !important;
	}
  
  	div.forFlow {
      	margin: 10px 16px 10px 16px !important;
    }
}
