css:
@-moz-document domain("web.whatsapp.com") {
body {
    background: none;
    background-color: #546E7A;
}
.pane-header.pane-list-header {
 	background-color:   #37474F; 
}
.message-text strong {
 	color: white;   
}
#startup,
#initial_startup {
    background-color: #455A64;
}
.media-document-text {
    color: white;
}
.intro {
    background-color: #37474F;
}
.chatlist-panel-body {
    background-color: #546E7A;
}
.menu-icons .menu-item {
    background-color: rgba(0, 0, 0, 0);
}
.chatlist .chat:hover,
.chatlist .chat.hover {
    background-color: #37474F;
}
.drawer-starred .drawer-body {
    background-color: #263238;
}
.starred-msg-author,
.starred-title-divider,
.starred-msg-chat,
.starred-title-meta {
    color: white;
}
.intro a {
    color: #40C4FF;
}
.intro:after {
    border-top: 6px solid #40C4FF;
}
.app .pane-two {
    width: 100%;
}
.pane-chat-body.selectable {
    background-color: #546E7A;
}
.checkbox.checked {
    background-color: #78909C;
    border-color: #00E5FF;
}
.checkbox {
    border-color: #78909C;
}
.message-in .document-body {
    background-color: #546E7A
}
.document-meta {
    color: white;
}
.bubble-doc {
    background-color: #455A64;
}
.context-special.context-out {
    background: none;
}
.link-preview-source {
    color: rgba(255, 255, 255, 0.8);
}
.link-preview-body {
    background-color: #546E7A;
}
.link-preview-title,
.link-preview-description {
    color: white;
}
.compose-popup-panel,
.btn-background {
    background-color: #546E7A !important;
    color: white !important;
}
.mentions-overlay-container {
    background-color: #546E7A !important;
}
.message .message-text .emojitext .emoji {
    background-color: #455A64 !important;
}
.media-collection {
    margin-top: 0px;
}
.pane-chat-footer {
    border-left: 0px;
}
div.pane-one:not(:hover) {
    width: 76px;
    -webkit-transition: width 0.5s;
    /* Safari */
    
    transition: width 0.5s;
}
div.pane-one:not(:hover) .pane-list-controls {
    display: none;
    opacity: 0;
}
.pane-list-controls {
    display: inline;
    opacity: 1;
}
div.pane-one:hover .pane-list-controls {
    -webkit-transition: opacity 2.5s;
    transition: opacity 2.5s;
}
div.pane-one:hover {
    -webkit-transition: width 0.5s;
    /* Safari */
    
    transition: width 0.5s;
}
.drawer-header-offset {
    background-color: #37474F;
}
.media-body-container {
    background-color: #546E7A;
}
.media-collection {
    background-color: #455A64;
}
.btn-fill {
    background-color: #607D8B;
    color: white;
}
#main,
.pane-intro {
    width: inherit !important;
}
.text-input {
    position: relative;
}
.input-spacer {
    padding-top: 15px;
    padding-bottom: 15px;
}
.list-action,
.emoji-panel-body,
.menu-tab,
.gif-search-container {
    background-color: #263238;
}
.c0 {
    background-color: #263238 !important;
}
.backdrop {
    background-color: rgba(96, 125, 139, 0.85);
}
.popup,
.popup-contents {
    background-color: #263238;
    color: white;
}
.input-wrapper {
    border-bottom: 0px !important;
}
.message-out .document-body {
    background: #546E7A;
}
.label-text {
    color: white !important;
}
.emojitext,
.popup-title,
.gif-section-title {
    color: white;
}
.message-system-e2e {
    background-color: #607D8B;
}
.popup-controls-item {
    color: white;
    transition: box-shadow .18s ease-out, background .18s ease-out;
}
.btn-default,
.btn-round {
    background-color: #546E7A;
}
.well-chat-secondary {
    margin-top: 5px;
}
.title,
.multi-count {
    color: white;
}
.header-title,
.chat-time-title {
    color: white;
}
.pane-list-body,
.multi-controls {
    background-color: #263238;
}
.empty {
    background-color: #455A64;
    color: white !important;
}
.drawer-settings .drawer-body {
    background-color: #263238;
}
.icon-x,
.icon-settings-blocked,
.icon-star-btn,
.icon-delete,
.icon-forward,
.icon-download,
.icon-settings-notifications,
.icon-settings-wallpaper,
.icon-settings-blocked,
.icon-settings-help,
.icon-hide {
    background-color: #90A4AE;
    border-radius: 10px;
}
.drawer {
    background-color: #263238;
}
.drawer-header {
    background-color: #607D8B;
}
.pane-subheader {
    background-color: #263238;
}
.list-title {
    background-color: #546E7A;
    color: white;
}
.drawer-section {
    background-color: #263238;
    color: white;
}
.row-body {
    color: white;
}
.well {
    background-color: #263238;
}
.row {
    margin-bottom: 0 !important;
}
.drawer-section-body {
    padding-top: 0px !important;
}
.drawer-section-title,
.drawer-section-body {
    background-color: #263238;
    color: white;
    margin-bottom: 0px;
}
.row {
    margin-bottom: 0px;
}
.hint {
    background-color: #263238;
    color: white;
}
.app-wrapper::after {
    background-color: #37474F;
}
.search-container {
    background-color: #263238;
    color: white;
}
.input-search {
    background-color: #263238;
    color: white;
}
.cont-input-search {
    background-color: #263238;
    color: white;
    border: 0px !important;
}
.icon-search {
    color: white;
}
.pane-intro {
    background-color: #263238;
}
.media-panel-header,
.media-viewer-thumbs {
    background-color: #263238;
}
.media-viewer-thumbs-container {
    margin-top: 0px;
}
.media-content {
    background-color: rgba(69, 90, 100, 0.85);
}
.pane-intro:after {
    border: 0px;
}
.incoming-msgs {
    background-color: #78909C;
}
.drawer-header-popup {
    background-color: #78909C;
}
.butterbar-notification {
    background-color: #78909C;
    height: 50px;
}
.butterbar-title,
.butterbar-text {
    color: white;
    opacity: 0.8;
}
.intro-title {
    color: white;
    opacity: 0.7;
}
.intro-image {
    border-radius: 180px;
}
.menu-item,
.icon-search,
.icon-smiley,
.icon-status,
.icon-ptt,
.icon-send {
    background-color: #90A4AE;
}
.icon-search,
.icon-smiley,
.icon-status,
.icon-ptt {
    border-radius: 30px;
}
.icon-send {
    border-radius: 5px;
}
.tail-container {
    opacity: 0;
}
.active {
    background-color: #546E7A !important;
}
.send-logs-input {
    background-color: #263238 !important;
}
.pane-header {
    background-color: #263238;
}
.chat {
    background-color: #263238;
    color: white;
}
.chat-title,
.selectable-text {
    color: white;
}
.chat-meta,
.chat-status,
.message-datetime {
    color: white !important;
    opacity: 0.7;
}
.hover {
    background-color: #78909C !important;
}
.pane-chat-body {
    background: none;
    background-color: #263238;
    color: white;
}
.bubble-image {
    background-color: #90A4AE;
    border-radius: 5px;
}
.tail-container {
    color: #90A4AE;
}
.icon-forward-chat {
    background-color: #455A64;
}
.bubble-text {
    background-color: #455A64;
    border-radius: 5px;
}
.message-system-body {
    background-color: #455A64;
    color: white;
    font-weight: bold;
}
.context-icon {
    background-color: #90A4AE;
    border-radius: 10px;
}
.context.context-in,
.context.context-out {
    background: none;
}
.dropdown {
    background-color: #263238;
}
.dropdown-compact {
    background-color: #263238;
    color: white;
}
.menu-shortcut {
    background-color: #90A4AE;
    color: white;
}
.block-compose {
    background-color: #263238;
}
.input-emoji {
    background-color: #455A64;
    color: white;
    border-radius: 5px;
    padding-left: 8px;
}
.input-container {
    background-color: #455A64 !important;
    border-color: #455A64 !important;
}
}
