css:
@-moz-document domain("voxed.net") {
body {
    background-color: #9c0c3f;
    color: #f1b5b9;
    font-family: Courier;
}
a {
    color: #d56dd4;
}
#header .is-dropdown-submenu {
    border: none;
}
.remodal {
    background-color: #940833;
    color: #62ab5f;
    box-shadow: 0px 0px 20px #000;
}
.remodal textarea,
.remodal input[type=text],
.remodal select {
    background-color: #bd0d68;
    border-color: #991358;
    color: #ffffff;
}
.remodal label {
    background-color: #da3e75 !important;
}
.title-bar,
.top-bar {
    box-shadow: none;
}
.title-bar,
.top-bar,
.top-bar ul {
    background-color: #e6295a;
}
.title-bar #config-panel a,
.top-bar #config-panel,
.top-bar ul a {
    color: #9a1818;
}
.title-bar .category-menu>li:hover,
.top-bar .category-menu>li:hover {
    background-color: #f16388;
}
.vox-single .vox-view .content .text h1 {
    color: #ff6f79;
}
.vox-single .vox-view .meta .label.secondary {
    background-color: #c43561;
}
.vox-single .vox-comments {
    background-color: #d0314b;
}
.vox-single .vox-comments .comments-list .comment:hover {
    background-color: #9d083d;
}
.vox-single .vox-comments .comments-list .comment .meta .created_at {
        color: #f1a9c3;
}

.vox-single .vox-comments .comments-list .comment .meta .author { 
    color: #fe5b66
}
.vox-single .vox-comments .comments-list .title {
    background-color: #e15b61;
    color: #F0F0F0;
}
.vox-single .vox-comments .comments-box {
    background-color: #be1420;
    color: #d91926;
    border: none;
}
.attach-vox,
.vox-single .vox-comments .comments-box textarea {
    background-color: #9d0711;
    border-color: #cc3b45;
    color: #ffe9e9;
}
.vox-single .vox-comments .comments-box textarea:focus {
    background-color: #950c16;
}
.vox-single .vox-view .breadcrumbs,
.vox-single .vox-view .meta {
    background-color: #e70343;
    color: #F0F0F0;
}
.vox-single .vox-view .meta span.report a {
    color: #ffffff;
}
i.icon-flag {
color: #fa2e2e;
}

.vox-single .vox-comments .comments-list .comment .meta .label.secondary {
    background-color: #232830;
}
.comment-hover {
    background-color: #232830;
    color: #F0F0F0;
    border-color: #232830;
}
.vox-single .vox-view .content .image a {
    background-color: #e84880;
    border-color: #e96090;
}
.vox-list .vox .over {
    border-width: 4px;
    border-style: solid;
    border-color: rgba(10, 10, 10, .5);
    background-color: rgba(10, 10, 10, .2);
}
.vox-list .vox:hover .over {
    background-color: rgba(10, 10, 10, .0);
}

.title-bar .button, .top-bar .button {
    display: block;
    color: #ffd4e5;
    letter-spacing: .01em;
    border-radius: 0;
    text-transform: uppercase;
    font-weight: 700;
    margin: 0!important;
    padding: .72rem 1rem;
    line-height: calc-rem(1);
    font-size: calc-rem(1);
}
.button {
    display: inline-block;
    cursor: pointer;
    -webkit-appearance: none;
    transition: background-color .25s ease-out,color .25s ease-out;
    border: 1px solid transparent;
    border-radius: 2px;
    padding: .85em 1em;
    margin: 0 0 1rem;
    font-size: .9rem;
    background-color: #8a030d;
    color: #fff;
}
.vox-single .vox-view .content .text h1 {
    font-family: "Brush Script MT", cursive,"Helvetica Neue Light","Helvetica Neue",Helvetica,Arial,Cambria, Georgia, serif;;
}
.new-vox .form-field label {
    display: block;
    background-color: #de44a9;
    color: #ffbce8;
    padding: 2px 5px;
    font-size: 10px;
    text-align: center;
}
span.role.primary.label {
    display: inline-block;
    padding: .33333rem .5rem;
    font-size: .8rem;
    white-space: nowrap;
    cursor: default;
    border-radius: 2px;
    background: #db50e4;
    color: #fefefe;
}
.breadcrumbs a {
    color: #e266ea;
}
#logo img {
    content: url('https://k61.kn3.net/7/9/E/2/B/7/944.png');
}
.vox-single .vox-comments .comments-list .comment .text .attach_link {
    border: none;
}
.vox-single .vox-comments .comments-list .comment .text>img {
    border-radius:5px;
}
.span.hide-for-small-only {
    background-color: #ffffff;
}

.is-dropdown-submenu-parent.is-down-arrow>a::after {
    content: '';
    display: block;
    width: 0;
    height: 0;
    border: 5px inset;
    border-color: #bc2929 transparent transparent;
    border-top-style: solid;
    border-bottom-width: 0;
    position: absolute;
    top: .825rem;
    right: 5px;
}
.span.tags {
    color: #db4dec;
}

.button:focus, .button:hover {
    background-color: #da202d;
    color: #fff;
}
.vox-list .vox:hover h3 {
    background-color: #f16d9f;
}
.comment-hover {
    background-color: #950c16;
    color: #fcc0c4;
    border-color: #cc3b45;
}
.vox-single .vox-comments .comments-list .comment .meta .label.secondary {
    background-color: #a500b8;
}

}
