#mnuBlueBar {
    background-color: #23a71a;
    background-image: linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -o-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -moz-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -ms-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,#075409),color-stop(1,#13ad19));
    clear: left;
    height: 32px;
    color: #fff;
    padding: 0;
    margin: 0;
    font-family: cursive, sans-serif;
    text-decoration: none;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 999;
    box-shadow: 0 2px 2px #444;
    -moz-box-shadow: 0 2px 2px #444;
    -o-box-shadow: 0 2px 2px #444;
    -webkit-box-shadow: 0 2px 2px #444;
}
