css:
@-moz-document domain("v2ex.com") {
/** 屏蔽不需要的内容 **/

#cbtf,
ins,
.feedback_container,
/* #Rightbar .sidebar_units
{
	display: none;
} */
/** 全局设置  **/

#Wrapper

{
	background: #f1f1f1!important;
}

.content
{
	max-width: 1200px;
}
/** 标签 **/

#Tabs a
{
	font-size: 15px;
	padding: 7px;
}

#Main> .box >div:nth-child(2) a
{
	font-size: 16px;
}

.box
{
	border-radius: 10px;
	box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
	-webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
	-moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
}
#Main,
#Rightbar
{
    animation-name: left_logoR;
	-webkit-animation-duration: .5s;
	-moz-animation-duration: .5s;
	animation-duration: .5s;
	-webkit-animation-timing-function: ease;
	-moz-animation-timing-function: ease;
	animation-timing-function: ease;
}

@-webkit-keyframes left_logoR
{
	0%
{
		-webkit-transform: translateY(64px);
		-moz-transform: translateY(64px);
		transform: translateY(64px);
		opacity: 0;
	}
	50%
{
		opacity: 0;
	}
	100%
{
		opacity: 1;
	}
}
/** 左边内容  **/

#Main .box
{
	padding: 20px;
}

#Main > .box > .item
{
	/* 	border:1px solid red; */
	
	padding: 15px 10px;
}

#Main .box .cell:hover
{
	box-sizing: border-box;
	background: #f8f8f8;
}

#Main .box .item_title
{
	font-size: 17px;
}

#Main .box .item_title a
{
	color: #4d5256;
	margin-left: 10px;
}

#Main .box .small
{
	font-size: 15px;
	margin-left: 10px;
	color: #aaa;
}

#Main .box .small>a
{
	background: rgba(0, 162, 100, .6);
	font-size: 13px;
	color: white;
	padding: 5px;
}

#Main .box .small>strong a
{
	font-size: 13px;
	color: rgb(79, 107, 186);
	padding: 5px;
}

#Main a.count_livid:link,
#Main a.count_livid:active
{
	line-height: 15px;
	font-size: 15px;
	font-weight: normal;
	color: white;
	background-color: #67708E;
}

.sep5
{
	height: 8px;
}
/* 左边内容 - 详细页*/

.gray
{
	font-size: 14px;
}

.gray a,
#Main a.dark:link,
#Main a.dark:visited,
#Main a.dark:active
{
	color: #4f6bba;
	font-size: 14px;
}

#Main a.dark:hover
{
	text-decoration: underline;
}
}
