.PanelScroller .scrollContainer, .PanelScrollerOff .panel, #loginBar,
#loginBar #loginBarHandle, .pollBlock .pollOption label:hover,
.pageNavLinkGroup, #DiscussionListOptionsHandle a, .navigationSideBar {
