css:
/*
 * Make Delicious better with a decent design
 * by Jack NUMBER, inspired by Tunghsiao Liu (previous Delicious designer)
 * https://sparanoid.com/work/delicious/
 *
 * Better design, design normalized, easy to use
 */

@-moz-document url(https://del.icio.us/) {
  /* Remove ugly textured background */
  body {
      background: #F5F5F5;
  }

  /* Remove hasbeen border-radius */
  .expanding,
  .expanding li a,
  div.profileInfo,
  div.searchPanel button,
  div.searchPanel input,
  div.tabMenu ul li a,
  .textFldsRow input[type="text"],
  .textFldsRow input[type="password"],
  .textFldsRow textarea,
  div.signInFlds input,
  .saveLinkPop textarea,
  #DELI_link_info input,
  #DELI_link_info textarea,
  #DELI_link_info #DELI_link_url input,
  .DELI_tag_container,
  .saveLinkPop select,
  div.homeBtns a,
  div.navPanel ul li.signupBtn a,
  .deliciousMessage,
  div.signBtnPan button,
  div.homeBtns a,
  div.profilePic,
  div.profilePic img,
  .notificationsRow img,
  .notificationsPanel button,
  a.unFollowBtn,
  a.unfollowBtnSmall,
  a.followBtn,
  a.followBtnSmall
  {
      border-radius: 0;
  }

  div.searchPanel input {
      box-shadow: none;
  }

  /* Hide ads area (tested with AdBlockPlus) */
  div[class*="googleAd"],
  div[class*="bookmark-ad"],
  div.articleRightPanel {
      display: none;
  }

  div.contentPanel {
      padding-top: 0;
  }

  div.profileContent div.container,
  div.homeContentPan div.container {
      padding-right: 10px;
  }

  /* CTA "Add bookmark" */
  div.navPanel ul li.toplink:last-child {
      display: inline-block;
      padding: 5px 15px;
      margin-left: 15px;
      font-size: 16px;
      line-height: 32px;
      background: #fff;
      cursor: pointer;
  }

  div.navPanel ul li.toplink:last-child,
  div.navPanel ul li.toplink:last-child a {
      color: #0076e8;
  }

  div.navPanel ul li.toplink:last-child:hover,
  div.navPanel ul li.toplink:last-child:hover a {
      color: #0a539a;
  }

  div.navPanel ul li.toplink:last-child i.fa {
      margin-right: 5px;
  }

  /* reset */
  button,
  input,
  textarea,
  select {
      font-size: 14px;
      font-weight: normal;
  }

  /* Reduce title */
  div.profileMidpanel h1 {
      font-size: 20px;
  }

  /* main menu */
  .expanding li a {
      background: transparent;
  }

  /* Profile details */
  div.profileInfo {
      background: #fff;
      box-shadow: none;
  }

  div.profileLinks {
      padding-top: 2px;
  }

  p + div.profileLinks {
      padding-top: 10px;
  }

  /* Link item */

  .articleInfoPan p:nth-child(2) a {
      color: #868484;
  }

  /* tag list */
  ul.tagName {
      padding-top: 5px;
  }

  ul.tagName li a {
      color: #868484;
  }

  /* popup */
  #DELI_banner #DELI_add_link,
  #delAlert .alert .con .btns button {
      background: #0076e8;
      padding: 5px 15px;
      display: inline-block;
      border: none;
      border-radius: 5px;
      font-size: 16px;
      line-height: 32px;
      color: #fff;
      cursor: pointer;
  }

  #delAlert .alert,
  .popcon .pop {
      border: none;
      border-radius: 0;
      max-width: 550px !important;
  }

  #delAlert .alert .con .btns,
  .popcon .saveSocialLinks,
  #DELI_banner
  {
      padding: 10px;
      background: #EEEEEE;
  }

  /* popup fields */
  div.signInFlds input,
  .saveLinkPop textarea,
  #DELI_link_info input,
  #DELI_link_info textarea,
  #DELI_link_info #DELI_link_url input,
  .DELI_tag_container,
  .saveLinkPop select
  {
      border: none;
      border-bottom: 1px solid #c4c4c4;
      background: transparent;

  }

  #DELI_link_info #DELI_link_url input:hover {
      border-color: #c4c4c4;
  }

  div.signInFlds input:focus,
  .saveLinkPop textarea:focus,
  #DELI_link_info input:focus,
  #DELI_link_info textarea:focus,
  #DELI_link_info #DELI_link_url input:focus,
  .DELI_tag_container.focused,
  .saveLinkPop select:focus
  {
      box-shadow: none;
      border-color: #0076e8;
  }


  /* popup/delete */
  #delAlert .alert .con {
      border-radius: 0;
  }

  #delAlert .alert .con .head {
      border: none;
      display: none;
  }

  #delAlert .alert .con .msg {
      padding: 30px;
      font-size: 15px;
      line-height: 23px;
      color: #000;
  }

  #delAlert .alert .con .btns {
      height: auto;
      border: none;
  }

  /* popup/edit */
  .popcon .pop {
      padding: 30px;
      padding-bottom: 0;
  }

  @media (max-width: 1000px) {
      .popcon .pop {
          margin: auto;
      }
  }

  div.signInFlds {
      margin: 0;
      padding: 0;
  }

  div.popInner {
      padding: 0;
      margin: 0;
  }

  div.popInner h3 {
      display: none;
  }

  .saveLinkPop label {
      font-size: 12px;
      color: #000;
      text-transform: uppercase;
      font-weight: bold;
  }

  .saveLinkPop label span {
      text-transform: none;
      font-weight: normal;
  }

  .popcon .saveSocialLinks {
      background: #eee;
      margin: 0 -30px;
  }

  .popcon .publicBtn {
      background: transparent;
      color: #0b79e5;
  }

  .popcon .privateBtn {
      background: transparent;
      color: #e8a923;
  }

  /* popup/add */
  .notRegisterPan {
      display: none;
  }

  #DELI_banner {
      width: 100%;
      box-sizing: border-box;
      margin: 0 !important;
      left: 0;
      bottom: 0;
  }

  #DELI_banner #DELI_add_link {
      width: auto;
      text-shadow: none;
      box-shadow: none;
  }

  #DELI_link_info #DELI_link_title {
      margin-top: 0;
      margin-bottom: 15px;
  }

  #DELI_link_info input,
  #DELI_link_info #DELI_link_url input,
  #DELI_link_info #DELI_link_comment {
      padding: 9px;
      width: 100%;
      box-sizing: border-box;
  }

  #DELI_mist {
      opacity: 0.5 !important;
      background: #45484d !important;
      background: -moz-linear-gradient(-45deg, #45484d 0%, #000000 100%) !important;
      background: -webkit-linear-gradient(-45deg, #45484d 0%,#000000 100%) !important;
      background: linear-gradient(135deg, #45484d 0%,#000000 100%) !important;
  }

  #DELI_save_link_slidedown {
      box-shadow: 0 0 8px rgba(0,0,0,0.5) !important;
      margin-left: 0 !important;
      left: calc(50% + 350px) !important;
      top: calc(50% + 275px) !important;
      transform: translateX(-50%) translateY(-50%) !important;
      background: white !important;
  }

  #DELI_banner #DELI_add_link,
  #DELI_banner #DELI_add_link:focus,
  #DELI_banner #DELI_make_link_private,
  #DELI_banner #DELI_make_link_private:focus
  {
      border: 0;
      box-shadow: none;
  }

  .DELI_dropdown {
      top: 50px;
      max-height: 145px;
  }

  #DELI_close {
      background-color: white !important;
  }

  #DELI_close:hover {
      background-color: #F5F5F5 !important;
  }

  /* settings fields */
  .textFldsRow input[type="text"],
  .textFldsRow input[type="password"],
  .textFldsRow textarea
  {
      border: none;
      border-bottom: 1px solid #c4c4c4;
      background: transparent;

  }

  .textFldsRow textarea {
      height: 75px;
  }

  /* home */
  div.articleLeftPanel {
      width: 100%;
  }

  .deliciousMessage {
      box-shadow: none;
      background: #fff;
  }

  /* signin & signup */
  #user-login-form {
      margin-top: 5px;
  }

  div.signInFlds {
      border: none;
  }

  #usersignup,
  .loginPanel {
      margin: 0 auto;
      max-width: 550px;
  }
}
