@-moz-document domain("thepiratebay.org"), 
domain("thepiratebay.mn"), 
domain("thepiratebay.gd"), 
domain("thepiratebay.la"), 
domain("thepiratebay.gs"), 
domain("thepiratebay.am"), 
domain("thepiratebay.vg"), 
domain("thepiratebay.se"), 
domain("ahoy.one"), 
domain("pirateproxy.vip"), 
domain("pirateproxy.cc"), 
domain("pirateproxy.tv"), 
domain("thepiratebay.red"), 
domain("ikwilthepiratebay.org"), 
domain("tpb.proxyduck.co"), 
domain("tpb.patatje.eu"), 
domain("thepirate.zone"), 
domain("thehiddenbay.xyz"), 
domain("duckingproxy.eu"), 
domain("tpbmirror.us"), 
domain("urbanproxy.eu"), 
domain("thehiddenbay.cc"), 
domain("piratebayblocked.com"), 
domain("pirateproxy.llc") {

body {
    font-family: Verdana, Helvetica;
    background: #131518;
    color: #dbdbdb;
}
#header {
	margin-top: 15px;
    margin-bottom: 15px;
}
#TPBlogo {
   	background-image: url(https://upload.wikimedia.org/wikipedia/commons/archive/1/16/20081206012354%21The_Pirate_Bay_logo.svg) !important;
    background-repeat: no-repeat;
    background-size: 89%;
    width: 0;
    padding: 40px;
}

#q {
    margin-bottom: 10px;
    width: 1200px;
    margin: auto;
}
#main-content {
    margin-left: 0px;
    margin-right: 0px;
    -webkit-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
    -moz-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
    box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
}
#fp h1 a {
    background: url(https://upload.wikimedia.org/wikipedia/commons/thumb/f/fb/The_Pirate_Bay_logo_bw.svg/2000px-The_Pirate_Bay_logo_bw.svg.png) no-repeat;
    background-size: 95%;
}
#q .inputbox {
    background: rgba(14, 16, 21, 0.5);
    border: 1px solid rgb(0, 167, 255);
    color: #f1f1f1;
    border-radius: 0;
    padding: 5px;
    padding-left: 10px;
    font-size: 12px;
}
#fp #inp input {
    background: rgba(14, 16, 21, 0.5);
    border: 1px solid rgb(0, 167, 255);
    color: #f1f1f1;
    border-radius: 0;
    padding: 5px;
    padding-left: 10px;
    font-size: 12px;
}
#detailsouterframe {
    width: 100%;
}
table#searchResult th, table#searchResult td {
    border: 1px solid #000;
}
table#searchResult tr {
    background: #0e0f11;
}
table#searchResult tr.alt {
    background: #191b1f;
}
table#searchResult td.vertTh {
    color: #FFF;
}
table#searchResult tr:hover {
    background: #25282e;
}
thead#tableHead th {
    background: #0f4967;
}
thead#tableHead a {
    color: #FFF;
}
.header a {
    color: #FFF;
}
a {
    color: #00bbff;
    border-bottom: none;
}
a, a:link, a:visited, a:focus {
    color: #00bbff;
    border-bottom: none;
}
a:hover {
    border-bottom: 1px solid #FFF;
    color: #00bbff;
}
.detDesc a:hover {
    color: #FFF;
}
h2 {
    border-bottom: none;
    background: none;
    width: 1200px;
    margin: auto;
}
#browseContainer a {
    color: #00bbff;
}
#browseContainer dt a {
    color: #dbdbdb;
}
#browseContainer a:hover {
    border-bottom: 1px solid #FFF;
}
#comments .comment {
    background: #0e0f11;
    padding: 10px;
}
div[id^="comment-"] {
    background: #0e0f11;
    border: 1px solid #000;
    margin-bottom: 10px;
}
#comments p.byline {
    color: #4e5456;
}
#categoriesTable {
    width: 1200px;
    background: #0e0f11;
    border: 1px solid #000;
}
#details {
	background-color: #191b1f
}
.nfo {
	background-color: #0e0f11;
    border: 1px solid #000;
    width: 98%;
}
a {
    color: #FFF;
    text-decoration: none;
    border-bottom: 1px dotted #FFF;
}
#details dt {
    color: #4e5456;
     border-bottom: none;
}
#details dd {
     border-bottom: none;
}
#detailsframe #title {
    background: #0f4967;
    border-bottom: 1px solid #000;
}
#detailsframe {
    border: 1px solid #000;
    background: #fff0;
    margin-top: 0;
}
#details h4 {
    color: #fff;
    margin-bottom: 10px;
}
.download a {
    color: #30faff !important;
}
select {
	background: rgba(14, 16, 21, 0.5);
    border: 1px solid rgb(0, 167, 255);
    color: #f1f1f1;
    border-radius: 0;
    padding-left: 5px;
    font-size: 12px;
}
input[type="submit"] {
	background-color: #0f1113;
    display: inline-block;
    color: #b1b1b1;
    padding: 5px 12px;
    margin-bottom: 0;
    font-size: 11px;
    font-weight: 400;
    line-height: 1.42857143;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    cursor: pointer;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-image: none;
    border: 1px solid rgb(0, 167, 255);
}
input[type="submit"]:hover {
		color: #00bbff;
}
.searchBox {
    background-color: rgba(14, 16, 21, 0.5) !important;
    border: 1px solid rgb(0, 167, 255);
    padding: 5px;
    color: white;
    background-image: none;
}
}
