body.greyBody {
    background-color: DarkGoldenRod;
    background-image: linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -o-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -moz-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -ms-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,DarkGoldenRod),color-stop(1,GoldenRod));
    margin: auto;
}

#mnuBlueBar {
    background-color: DarkGoldenRod;
    background-image: linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -o-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -moz-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -ms-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,LightSteelBlue),color-stop(1,#778899));
    clear: left;
    height: 32px;
    color: white;
    padding: 0;
    margin: 0;
    font-family: Impact, Charcoal, sans-serif;
    text-decoration: none;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 999;
    box-shadow: 0 2px 2px #444;
    -moz-box-shadow: 0 2px 2px #444;
    -o-box-shadow: 0 2px 2px #444;
    -webkit-box-shadow: 0 2px 2px #444;
}
#c_bar {
    background-color: GhostWhite;
    background-image: linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -o-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -moz-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -ms-linear-gradient(bottom,#2e466b 0%,#395885 100%);
    background-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,GhostWhite),color-stop(1,Gainsboro));
    border-left: solid 1px #131c24;
    border-right: solid 1px #131c24;
    border-bottom: solid 1px #131c24;
    display: inline-block;
    display: block;
    overflow: hidden;
  	font: normal normal bold 12px/normal "Bad Script", Helvetica, sans-serif;
    color: Purple;
}
