css:
@-moz-document url-prefix("https://www.google.") {
/* display content */

g-section-with-header,
#res .exp-outline,
#res div[jsl*="$t "],
#extrares,
#rso .mod~.g,
#rso .g.mnr-c.g-blk,
#taw,
#bottomads,
#imagebox_bigimages,
#foot tbody tr>td:not([class*='b']) span,
#foot tbody tr>td[class*='b'] span[class*='ch'],
#extabar,
.rc > .s + div,
#test {
    display: none!important;
    position: fixed!important;
    z-index: -2;
}
/* global set */

body {
    position: relative;
    background: transparent;
}
body:after {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: #f1f2f3;
    z-index: -1;
}
#rcnt {
    animation-name: Loading;
    -webkit-animation-duration: .5s;
    -webkit-animation-timing-function: ease;
}
/* header */
.RNNXgb{
    box-shadow:none!important;
    border-radius:0!important;
}
#searchform {
    position: fixed!important;
}
div.sfbg,
div.sfbgg {
    height: 80px!important;
}
.sfbgg,
#hdtbSum {
    background: #e5e5e5!important;
}
/* content left */

#rcnt {
    margin-top: 10px;
}
#rso .iUh30 {
    font-size: 13px;
}
#rso .bkWMgd .g,
#rso .srg .g {
    width: 632px;
    margin: 10px;
    margin-left: -17px;
    padding: 10px 15px;
    padding-top: 5px;
    background-color: #fdfdfd;
    border: 1px solid #e6e5e5;
    box-sizing: border-box;
}
#rso .srg .g:first-child {
    margin-top: 0px;
}
#rso .bkWMgd .g h3,
#rso .srg .g h3 {
    display: inline-block;
    position: relative;
    text-decoration: none;
    color: #1151a7;
}
#rso .bkWMgd .g h3:after,
#rso .srg .g h3:after {
    content: "";
    position: absolute;
    border-bottom: 1px solid;
    bottom: 3px;
    left: 100%;
    width: 0;
    -webkit-transition: width 350ms, left 350ms;
    -moz-transition: width 350ms, left 350ms;
    transition: width 350ms, left 350ms;
}
#rso .bkWMgd .g h3:hover:after,
#rso .srg .g h3:hover:after {
    left: 0;
    width: 100%;
    -webkit-transition: width 350ms;
    -moz-transition: width 350ms;
    transition: width 350ms;
}
/**  visited  **/

#rso .bkWMgd .g h3:visited:after,
#rso .srg .g h3:visited:after {
    content: "";
    position: absolute;
    border-bottom: 1px solid #660099;
    bottom: 2px;
    left: 100%;
    width: 0;
    -webkit-transition: width 350ms, left 350ms;
    -moz-transition: width 350ms, left 350ms;
    transition: width 350ms, left 350ms;
}
#rso .bkWMgd .g h3:visited:hover:after,
#rso .srg .g h3:visited:hover:after {
    left: 0;
    width: 100%;
    -webkit-transition: width 350ms;
    -moz-transition: width 350ms;
    transition: width 350ms;
}
a.fl:link,
.fl a,
.flt,
a.flt,
.gl a:link,
a.mblink,
.mblink b {
    color: #1151a7!important;
}
/******/

#res .r {
    line-height: 1.6;
}
#ires .hJND5c {
    margin-top: 0px;
    margin-bottom: 5px;
}
/* content right */

#rhs .kp-blk {
    background-color: #fdfdfd;
}
#rhs .kp-header {
    padding: 5px 20px;
}
/* foot */

#foot tbody tr>td {
    display: inline-block;
    width: 36px;
    height: 36px;
    margin: 0px 5px;
    line-height: 36px;
    cursor: pointer;
    color: #7593c6;
    background: white;
    box-sizing: border-box;
}
#foot #navcnt .cur {
    background: #7593c6;
    color: white;
}
#foot tbody tr>td:hover,
#foot tbody tr>td:hover a {
    outline: 1px solid ;
    text-decoration: none;
}
#foot tbody tr>td[class*='b'] {
    display: none;
}
/* animal */

@-webkit-keyframes Loading {
    0% {
        -webkit-transform: translateY(64px);
        opacity: 0;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}
}
