css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.delfi.lt") {

  body, table[width="398"] > tr > td, .comCap {
    background-color: #2a2a2a !important;
    color: #dcdccc !important;
  }

  body > div { text-align: left !important; }

  textarea, input[type="text"] {
    background-color: #2a2a2a !important;
    color: #dcdccc !important;
    border: solid 1px black;
  }
  
  body > div > table > tbody > tr > td > table {
    background-color: #2a2a2a !important;
  }

  .articleBody, .articleTitle, table[width="398"] {
    display: block;
    width: 800px;
  }
  

  a, a:hover, a:link, a:visited, .articleTitle {
    color: #f0dfaf !important;
  }

  iframe, .dsearch { display: none !important; }

  table[width="180"], td[bgcolor="#EEEEEE"] {
    display: none !important;
  }

}
