@-moz-document domain('wikipedia.org') {
    /* Wikipeida */
    body {
        font-family:'/*[[macsf]]*/';
    }

    /* Title */
    .mw-body h1, .mw-body h2, .mw-body h3, .mw-body h4, .mw-body h5 , .mw-body h6 {
        font-family:'/*[[mactitle]]*/';
    }

    /* Content */
    .mw-body p {
        font-family:'/*[[macsf]]*/';
        font-size: 15px;
    }
}

@-moz-document domain('wikipedia.org') {
    /* Wikipeida */
    body {
        font-family:'/*[[macsf]]*/';
    }

    /* Title */
    .mw-body h1, .mw-body h2, .mw-body h3, .mw-body h4, .mw-body h5 , .mw-body h6 {
        font-family:'/*[[mactitle]]*/';
    }

    /* Content */
    .mw-body p {
        font-family:'/*[[macsf]]*/';
        font-size: 15px;
        -webkit-text-stroke-width: 0.4px;
    }
}

@-moz-document domain('zhihu.com') {
    /* Zhihu */

    /* Title */
    h1[class*="QuestionHeader-title"] {
        font-family:'/*[[mactitle]]*/';
    }

    /* Content */
    div[class*="content"] {
        font-family:'/*[[macsf]]*/';
        font-size: 15px;
    }

    /* Head, Panel, Title */
    .answer-head, .zh-answers-title, .zm-meta-panel {
        font-family:'/*[[mactitle]]*/';
        font-size: 13px;
    }

    /* Tags */
    .zm-item-tag {
        font-family:'/*[[mactitle]]*/';
        font-size: 13px;
    }
}

@-moz-document domain('zhihu.com') {
    /* Zhihu */

