css:
@-moz-document url-prefix("https://evrl.to/offtop/") {
html, body {
    scroll-behavior: smooth;
    background: #010E16 url(http://i.imgur.com/izHq3xC.png) fixed no-repeat center top;
    /* color: #5D91A3; */
}

.gblock {
    background: rgba(8, 64, 88, 0.29);
    padding: 16px;
    margin: 16px 0 16px 0;
    border-radius: 2px;
    box-shadow: 0px 0px 0px 3px rgba(7, 32, 62, 0.11);
    position: relative;
}

.talks_message_body {
    line-height: 160%;
    color: #fff;
    font-size: 11px;
    margin: 0px 0 0 1px;
    word-wrap: break-word;
}

.feed-item-comments {
    background: rgba(8, 64, 88, 0.29);
    padding: 8px;
    border-radius: 0 0 2px 2px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
    margin: 1px 8px 0 8px;
}

#leftnav {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    background: rgba(8, 64, 88, 0.29) !important; 
    z-index: 1030;
    width: 60px;
    display: block;
}

.navbar-inverse {
    background: rgba(8, 64, 88, 0.29) !important;
    margin: 0;
}

.control-container {
    font-size: 11px;
    padding: 16px;
    background: rgba(8, 64, 88, 0.29) !important;
}

.feed-item-content {
    background: rgba(8, 64, 88, 0.29);
    padding: 16px;
    border-radius: 2px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
}

.get_more {
    text-align: center;
    padding: 8px 0;
    margin: 10px;
    background: rgba(8, 64, 88, 0.29) !important;
    font-size: 11px;
    color: #446D99;
    cursor: pointer;
}

.talkbox.gametalkbox {
    background: rgba(8, 64, 88, 0.29) !important;
    border-top: 1px solid rgba(8, 64, 88, 0.29) !important;
    border-radius: 0 0 2px 2px;
    margin: -8px -8px -8px -8px;
    padding: 8px;
}

.talkbox {
    background: rgba(8, 64, 88, 0.29) !important;
    position: relative;
}

.talk-reply {
    padding: 10px 10px 8px 10px;
    border-bottom: 1px solid rgba(8, 64, 88, 0.29) !important;
    position: relative;
}

input, textarea, select, .uneditable-input {
    display: inline-block;
    height: 14px;
    padding: 4px;
    font-size: 11px;
    color: #222;
    background-color: rgba(2, 15, 21, 0.34) !important;
    border: 1px solid rgb(10, 50, 74);
    color: #ffffff;
}

.feed-item-comments .feed-item-content {
    padding: 0;
    box-shadow: none;
    border-radius: 0;
    background-color: rgba(255, 255, 255, 0) !important;
}

.game-result .header {
    position: relative;
    margin: -16px 0 0 -16px;
    padding: 16px 0 16px 16px !important;
    border-bottom: 1px solid #eee;
    background: rgba(8, 64, 88, 0.29) !important;
    border-radius: 2px 2px 0 0;
}

.topbutton .file-topbtn:hover, .topbutton .file-topbtn:focus, .topbutton .file-topbtn.checked:hover, .topbutton .file-topbtn.checked {
    background: transparent !important;
    color: #ffffff !important;
    border-bottom: 2px solid #42A5F5;
}

body {
    margin: 0;
    font-family: Tahoma, Arial;
    font-size: 12px;
    color: #fff;
    padding-top: 50px;
    line-height: 1.2;
    height: 100%;
}

.navbar {
    border: 0;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0) !important;
}

.ilikeit_text.selected {
    background: rgba(2, 15, 21, 0) !important;
    opacity: 1.0;
}

.ilikeit_text:hover {
    background: rgba(2, 15, 21, 5) !important;
    opacity: 1.0;
}

.premium-rank {
    color: #ececec;
    font-weight: normal !important;
    float: none !important;
}

#leftnav .leftbuttons {
    background: rgba(54, 65, 81, 0);
    height: 100%;
    position: absolute;
    width: 60px;
    overflow: hidden;
    top: 40px;
    -webkit-transition: width 0.3s ease-out;
    transition: width 0.3s ease-out;
}

.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid rgba(221, 221, 221, 0.08);
}

.chat_user span {
    padding-left: 5px;
    text-shadow: 0 0 3px black;
}

element.style {
    background: rgba(255, 255, 255, 0) !important;
}




.feed-item-content {
    background: rgba(8, 64, 88, 0.29) !important;
    padding: 16px;
    border-radius: 2px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
}

.feed-item-comments {
    background: rgba(8, 64, 88, 0.29) !important;
    padding: 8px;
    border-radius: 0 0 2px 2px;
    box-shadow: 0 1px 2px rgba(7, 32, 62, 0.11);
    border: none;
    margin: 1px 8px 0 8px;
}

.talkbox .send_container .talk-addpoll-box, .talkbox .send_container #user-addpoll-box {
    padding: 4px;
    margin: 4px 0px 4px 0;
    font-size: 11px;
    background: rgba(8, 64, 88, 0.29) !important;
    display: none;
}

.poll_var:hover {
    background: rgba(8, 64, 88, 0.29) !important;
}

.smile-icon {
    background: url(http://www.travel-buddies.com/images/emoticons/smile.png);
    width: 14px;
    height: 14px;
    position: absolute;
    z-index: 1;
    right: 10px;
    top: 10px;
    opacity: 0.5;
    cursor: pointer;
}

.talks_message_bottom {
    line-height: 18px;
    font-size: 11px;
    color: rgba(255, 255, 255, 0.98);
    margin: 0px 0 0 1px;
    word-wrap: break-word;
}

.photo-icon {
    background: url(http://www.ur1.com/App_Themes/skin_1/images/up.gif);
    width: 14px;
    height: 14px;
    position: absolute;
    z-index: 1;
    right: 10px;
    top: 32px;
    opacity: 0.5;
    cursor: pointer;
}

.avatar_square_link img {
    float: left;
    width: 50px;
    height: 50px;
    border-radius: 100px;
}

.popover {
    font-family: Tahoma;
    font-size: 11px;
    line-height: 160%;
    background-color: rgb(8, 64, 88) !important;
}

.navbar-search .form-control {
    color: #fff;
    background-color: #fff;
    border: 1px solid #000;
    font-size: 12px !important;
    padding: 5px 9px 7px 9px !important;
    line-height: 14px !important;
    width: 243px !important;
    height: 28px;
}

.poll_var {
    padding: 2px;
    cursor: pointer;
    background-color: rgba(0, 0, 0, 0) !important;
}

.feed-item img {
    max-width: 99.9%;
    border: 1px solid rgba(255, 255, 255, 0) !important;
}

.fixed-talk-comment>.feed-item-content {
    background: rgba(8, 64, 88, 0.54) !important;
}

}
