/*** Status ***/
#listrow_status {min-height:20px;}
listrow_status span span {display:block; padding-top:10px;}

/* Beutezauber */
#listrow_status span[title^='Beutezauber']:before
{
    content: "(BZ) "; font-size:10px;
}

/* Heilkraft */
#listrow_status span[title^='Heilkraft']:before
{
  content: "(HK) "; font-size:10px;
}

/* Lebenspunkte + */
#listrow_status span[title^='Lebenspunkte +']:before
{
   content: "(LP+) "; font-size:10px;
}

/* Rückangriff */
#listrow_status span[title^='Rückangriff']:before
{
    content: "(RA) "; font-size:10px;
}

/* schnelle Bewegung */
#listrow_status span[title^='schnelle Bewegung']:before
{
    content: "(sB) "; font-size:10px;
}

/* Schutz */
#listrow_status span[title^='Schutz']:before
{
    content: "(Sch) "; font-size:10px;
}

/* Stärke + */
#listrow_status span[title^='Stärke +']:before {
    content: "(St+) "; font-size:10px;
}

/* Tarnung */
#listrow_status span[title^='Tarnung']:before
{
    content: "(Tar) "; font-size:10px;
}

/*** Neutrale ZustĆ¤nde ***/

/* Feueröl */
#listrow_status span[title^='Feueröl']:before
{
    content: "(Feu) "; font-size:10px;
}

/* Clankrieg */
#listrow_status span[title^='Clan']:before
{
    content: "(CK) "; font-size:10px;
}

/*** Negative ZustĆ¤nde ***/

/* KampfunfĆ¤hig */
#listrow_status span[title^='Kampfunfähig']:before
{
    content: "(KU) "; font-size:10px;
}

/* Vergiftung */
#listrow_status span[title^='Vergiftung']:before
{
    content: "(Gif) "; font-size:10px;
}

/* VerrĆ¤ter */
#listrow_status span[title^='Verräter']:before
{
    content: "(Ver) "; font-size:10px;
}

/* Lebenspunkte - */
#listrow_status span[title^='Lebenspunkte -']:before
{
    content: "(LP-) "; font-size:10px;
}

/* Ćberladen */
#listrow_status span[title^='Überladen']:before
{
    content: "(ĆB) "; font-size:10px;
}

/* StĆ¤rke - */
#listrow_status span[title^='Stärke -']:before
{
    content: "(ST-) "; font-size:10px;
}
/*** Status *******************************************************************/
#listrow_status span span
{
    font-size:0;
    position:relative;
        padding:1px;
}

/*** Restzeit ***/
#listrow_status span span:after
{
    font-size:10px;

    position:absolute;
    bottom:-12px;
    left:2;
}

#listrow_status span span[title$='(61 Minuten)']:after { font-weight: bold; content:'61mm'; }
