/* --- Freundes-Liste */

.friendlist a[href="#"][onclick*="tt(event,"]:after {
    content: attr(title);
    position: absolute;
    left: 310px;
    right: 5px;
    display: inline-block;
    color: rgba(0, 0, 0, 0.79);
    font-weight: bold;
    height: 25px;
    overflow: hidden;
    width: 400px;
    font-size: 10px;
    padding-left: 3px;

    padding-top:1px;
}

.friendlist a[href="#"][onclick*="tt(event,"][title="Kommentar verfassen"]:after {
    content: "Kommentar verfassen.";
    color: #707880;
    font-style: italic;
    font-size: 10px;
    padding-top: 0px;
    padding-left: 3px;
    background: transparent;
}
.friendlist + br + p + .friendlist a[href="#"][onclick*="tt(event,"]:after {
    left: 310px;

background: transparent;
    
}

