}

/***** Unterkuenfte von Laree *****/
.builtcaption, .editcaption, .visitcaption { position: absolute; right: 15px; top: 45px; color: transparent; font-size: 0px;  }
.builtcaption b {          border-radius: 4px;
        border: none;
        display: inline-block;
        color: white;
        padding: 2px 3px;
        text-decoration: none;
        font-size: 10px;
        margin: 0 1px;
        background-color: transparent;
}

.builtcaption a, .editcaption a, .visitcaption a  {

        border-radius: 4px;
        border: none;
        display: inline-block;
        color: #CC3300;
        padding: 2px 3px;
        text-decoration: none;
        font-size: 10px;
        margin: 0 1px;
        background-color: transparent;
}

.builtcaption b{
color: black;
}

.builtcaption a:hover, .editcaption a:hover, .visitcaption a:hover {
        color: #FF3300;
}

