css:
@-moz-document domain("geektimes.ru"), domain("habrahabr.ru"), domain("geektimes.com"), domain("habr.com") {
.tab-item_stacked {
    height: 50px !important;
    padding: 5px;
}

body.nl .layout__navbar {
    background-color: #f8fafb;
    height: 53px;
}

.layout__row_navbar {
    background-color: #f8fafb !important;
}

.column-wrapper_list {
    margin-top: 0;
}


.nav-links {    
    margin-top: 245px;
    margin-left: -135px;
    flex-direction: column;
	position: fixed;
    height: 300px;
}

.nav-links__item {    
    width: 100px;
    height: 100px;
    margin-right: 0;
}

.nav-links.hidden {    
    display: block !important;
}


.logo {
	width: 0px !important;
    margin-right: 0px !important;
}

.page-header_110 {
    height: 60px !important;
}

.tabs-menu__item-text {
    line-height: 40px;
	height: 42px !important;
}

.layout__elevator {
    height: 20px !important;
    top: 10px !important;
}

.column-wrapper {
    background-color: white;
    border-right: solid 1px #eaeaea;
    border-left: solid 1px #eaeaea;
    
}

.default-block {
    background: white !important;
}

.tabs__level_bottom {
    float: right;
}

.content_left {
    background-color: white;
    padding: 6px 32px 10px 20px;
    margin-top: -1px;

    border-bottom: solid 1px #eaeaea;
    border-top: solid 1px #eaeaea;
}

.nav-links__item-link {
    height: 40px;
    line-height: 40px;
}

body.nl .layout__navbar .main-navbar {
   max-width: 1400px;
   height: 53px;
}

body.nl .layout {
    background: #f4f6f9;
}

body.nl .layout__row_footer-links {
    margin-top: 0px !important;
    background: #f4f6f9;
}

.page-header {
    height: 90px;
    display: flex;
}

.page-header_wrapper {
    float: right;
    flex-grow: 1;
}

.page-header__buttons {
    padding-left: 10px;
    padding-right: 10px;
}

.page-header__info {
    padding-left: 10px;
    padding-right: 10px;
    order: -1;
    flex-grow: 0;
}


body.nl .layout__base {
    max-width: 1050px !important;
    background-color: #f4f6f9;
	border-left: 1px solid #eaeaea;
    border-right: 1px solid #eaeaea;
}

.sidebar_right {
    display: none;
}

.column-wrapper_tabs .sidebar_right {
    display: block;
    margin-top: -1px !important;
    margin-left: 0px !important;
}


.content_left {
    padding-right: 32px;
}

.toggle-menu__item-link {
    border-width: 1px;
}

.comment_plain {
    max-width: none;
    width: 100%;
}
}
