css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.easyelectronics.ru") {

  html, body { background-color: #000000; color: #BABDB6; }
  .row1 { background-color: #313533; }
  .row2 { background-color: #2E3436; }
  
  .quotecontent, .attachcontent { background-color: #404040; color: #BABDB6; }
  .codecontent { background-color: #222222; color: #2FC625; font-family: monospace, 'Courier New'; font-size: 14px; line-height: 20px;}
  
  a:link { color: #D05030; }
  a:visited { color: #B05070; }
  a:hover { color: #7ABEDA; } 
  a.topictitle:visited { color: #B05070; }
  
  .postauthor { color: #FFFFFF; }
  .postdetails { color: #AAAAAA; }
  .catdiv { background: #272C2D !important; }
  .cat { background-color: #272C2D; }
  
  .post, textarea { background-color: #2E3436; color: #BABDB6; }
  .helpline { background-color: #8E9397; }
}
