css:
@-moz-document domain("foorum.perekool.ee") {
.table-striped tbody tr:nth-of-type(2n+1),
blockquote {
    background-color: #eeeeee;
}
.even, body  {
    background-color: #ffffff;
}
body {
    font-size: 0.8em;
    font-family: Verdana;
    top: 0;
    margin: 0;
    background: none;
    color: black;
}
.list-group-item.active,
#bbpress-forums fieldset.bbp-form legend,
.btn-primary {
    background-color: #dddddd !important;
}

thead,
.bbp-reply-header.entry-title,
.page-numbers.current,
.btn.btn-warning,
.btn-warning-outline.active,
.btn-default.user-submit {
    background-color: #cccccc !important;
    border-color: #cccccc !important;
}

.btn-warning-outline,
a.page-numbers, 
span.page-numbers,
.page-numbers li a,
.page-numbers li span {
    background: none;
    border-color: #cccccc !important;
    color: inherit;
}


.input-group {
	display:block !important;
    width: 200px;
}

.form-control,
.input-group .form-control {
	width: 150px;
    float: left;
}

.navbar,
.parempoolnereklaam,
.site-footer,
.alert-warning,
body .container:first-child,
.wprc-container .wprc-switch,
#skin_sitehat,
#skin_right,
#skin_left,
#skin_background
.smartad_standard,
.smartad,
.smartad_overlay,
#google_ads_frame1,
.googleadspace,
.textbannerdiv,
iframe,
.bsaProContainer,
.list-group.side,
.row .col-sm-4.ml-sm-auto,
.alert-danger,
.korduvvastus
{
    display: none;
}

.korduvvastusfoorum {
	display: inline !important;
}

/*
.varemkirjutanud {
    float: left;
}
*/

body .container a {
	font-weight: 500;
    color: navy;
}

.entry-content p a, .entry-content p a:visited {
    color: blue;
}

.btn-warning-outline,
.bbp-reply-content.entry-content {
    border-color: #cccccc;
}
.bbp-reply-content.entry-content {
    border-width: 1px;
}
.bbppu-read,
.bbppu-unread,
.sticky {
    border-left: none;
}

.bbp-form, #new-post {
    width: 100%;
}

.table td, .table th, .topic h1 {
	font-size: inherit;
}

.bbp-breadcrumb-current,
.bbp-breadcrumb-sep,
.bbp-breadcrumb-home,
.bbp-breadcrumb-forum {
    font-size: 1rem;
    font-weight: 500;
}
.col-sm-8 {
	max-width: 100%;
    flex: none;
}

.gdrts-thumbs-theme-simple .gdrts-thumb .gdrts-thumb-link,
.gdrts-thumbs-theme-simple .gdrts-thumb .gdrts-thumb-count {
	font-size: 12px;
    float: left;
}

.gdrts-rating-block, 
.gdbbx-footer-meta {
	margin: 0 15px;
    padding: 0;
}

.loop-item-0 > .entry-content > div:first-child {
    display: none;
}
}
