@namespace url(http://www.w3.org/1999/xhtml);
/*
Pad-Dark
*/

@-moz-document regexp("(.*)pad(.*)") {
 body,
		#outerdocbody,
		#innerdocbody{
		background:#555555; 
	}
  #outerdocbody {
    background-color: #2d2d2d;
  }
  
  .sidedivdelayed {
    background-color: #202020;
    color: #333;
    border-right: 1px solid #333;
  }
  
  .toolbar {             
    background: #202020; 
    border-bottom: 1px solid #202020;
  }  
  
  .toolbar ul li a {
    background: #2d2d2d;
    background: -webkit-linear-gradient(#2d2d2d);
    background: -moz-linear-gradient(#2d2d2d);
    background: -o-linear-gradient(#2d2d2d);
    background: -ms-linear-gradient(#2d2d2d);
    background: linear-gradient(#2d2d2d);
    border: 1px solid #2d2d2d;
    color: #fff;
    min-height: 20px;
  }
  
  a {
		color: #4a9aa9;
  }
  
  #chatbox, #chaticon {
    border-right: 0px;
    border-top: 0px;
    border-left: 0px; 
  }
  
  #chaticon {
    background-color: #202020;
  }
  
  #chatlabel {
    color: #FFF;
  }
  
  .buttonicon {
    color: #FFF;
  }
  
  #chatcounter {
    color: #FFF;
  }
  
  #chatbox {
    background-color: #484848;
  }
  
  #titlebar {
    line-height: 16px;
    color: #F8F8F8;
    position: relative;
    bottom: 2px;
  }
  
  #titlecross, #titlesticky {
    color: #FFF;
  }
  
  #chaticon, #chattext {
    background-color: #5f5d5d;
  }
  
  #chattext {
    background-color: #5f5d5d;
     border: 0px;
  }
  
  #chatinput {
    border: 1px solid #2F2F2F;
  }
  
  .stickyChat {
    background-color: #484848 !important;
    right: 0 !important;
    top: 37px;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    height: auto !important;
    border-left: 1px solid #484848 !important;
    width: 185px !important;
  }
  
  #users, .toolbar {
    background: #111111;
  }
}
