css:
/* original code by Zebouski/UGoBoom - Google Docs Dark UI */
/* edited because I want something different */

@-moz-document url-prefix('https://docs.google.com/document/') {

#docs-chrome {
    background: #fff none repeat scroll 0% 0% !important;
    color: #333 !important;
}
#gb#gb a.gb_b,

/* Account email in top right corner */
.docs-presence-plus-widget-status

/* x total viewers text */
{
    color: #DDD !important;
}

/* Document title box */
.docs-title-input {
    background-color: #fff !important;
    border-radius: 2px !important;
    border: 1px solid #fff !important;
    border-top-color: #fff !important;
    font-style: normal !important;
    box-shadow: 0 1px rgba(255, 255, 255, 0.15), inset 0 1px rgba(255, 255, 255, 0.1) !important;
}
.docs-title-input:focus {
    color: black !important;
    background-color: #fff !important;
    border: 1px solid #fff !important;
    border-top-color: #fff !important;
}
.docs-title-untitled {
    display: none !important;
}

/* Main toolbar */
#docs-toolbar-wrapper {
    border-top: 1px solid #fff !important;
    border-bottom: 1px solid #fff !important;
    background-image: linear-gradient(#fff, #fff) !important;
    box-shadow: inset 0 1px rgba(255, 255, 255, 0.15) !important;
}

/* Toolbar's vertical dividers */
.goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid #fff !important;
}

/* search the menus color change */
#docs-omnibox-toolbar {
    color: #fff;
    background-color: #fff;
}
input.docs-omnibox-input.jfk-textinput {
    color: black;
    background-color: #fff;
}
docs.omnibox.autocomplete {
    color: #fff;
}

/* Background behind the document */
#docs-editor {
    background: #fff none repeat scroll 0% 0% !important;
}
.kix-page-compact::before {
    border-top: 1px #fff !important;
    border: 1px #fff !important;
}
.kix-paginateddocumentplugin-background docs-ui-unprintable {
    color: #fff !important;
    width: 794px;
    background-color: rgb(25, 25, 25);
    height: 900px;
    padding-top: 96px;
}

/* Document drop-shadow removal */
.kix-page-paginated,
.kix-paginateddocumentplugin.kix-paginateddocumentplugin-compact-mode {
    box-shadow: none !important;
}
.docs-explore-widget-button goog-inline-block goog-flat-button {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
.kix-document-top-shadow-inner {
    opacity: 0 !important;
}}
