css:
@namespace url(http://www.w3.org/1999/xhtml);

@font-face { 
  font-family: "KOverwatch"; 
  src: url(http://us.battle.net/forums/static/fonts/koverwatch/koverwatch.ttf) format('ttf');
}

@font-face { 
  font-family: "Overwatch"; 
  src: url(http://us.battle.net/forums/static/fonts/bignoodletoo/bignoodletoo.woff2) format('woff2'),
    url(http://us.battle.net/forums/static/fonts/bignoodletoo/bignoodletoo.woff) format('woff');
}

@font-face { 
  font-family: "Blizzard"; 
  src: url(http://us.battle.net/forums/static/fonts/blizzard/blizzard.woff2) format('woff2'),
    url(http://us.battle.net/forums/static/fonts/blizzard/blizzard.woff) format('woff'); 
}

@-moz-document domain("overstalk.io") {
  
  body {
    background-image: /*linear-gradient(to bottom, rgba(31, 35, 38, 0) 0px, rgba(31, 35, 38, 1) 1000px),*/ 
      url(https://us.battle.net/forums/static/images/backgrounds/background-overwatch-top.jpg),
      url(https://us.battle.net/forums/static/images/backgrounds/background-overwatch-repeat.jpg);
    background-position-x: /*center,*/ center, center;
    background-position-y: /*-200px,*/ -200px, -200px;
    background-repeat: /*no-repeat,*/ no-repeat, repeat-y;
    background-color: #1f2326;
    font-family: Blizzard, sans;
    color: #ccc;
  }
  
  nav.navbar {
    color: rgb(204, 204, 204);
    background-color: rgba(0, 0, 0, 0.35);
  }
  
  .navbar-light, .navbar-nav, .nav-link {
    color: rgb(154, 154, 154) !important;
    font-weight: lighter !important;
  }
  
  .navbar-brand {
    color: white !important;
  }
  
  .navbar-light .navbar-nav .active > .nav-link {
    color: rgb(204, 204, 204) !important;
  }
  
  h1, small {
    font-family: KOverwatch, Overwatch, Blizzard, sans-serif !important;
    font-size: 2.75rem;
    font-weight: 100;
    color: #ffffff;
    margin-top: 12px;
  }
  
  #os-filter-form {
    font-family: Blizzard, sans-serif !important;
    color: #ffffff;
  }
  
  div.form-group.os-post-meta label {
    padding-bottom: 4px;
    font-size: 0;
    display: inline-grid;
  }
  
  div.form-group.os-post-meta label img {
    margin: 4px
  }
  
  .blockquote {
    background-color: #00000050;
    border: 1px solid rgba(255,255,255,.15);
    border-width: 1px;
    border-style: solid;
    border-color: rgba(255, 255, 255, 0.15);
  }
  
  .blockquote div {
    display: flex;
    order: 1;
  }

  .blockquote div .bml-quote-date {
    order: 2;
    margin-left: auto;
  }
  
  div.card {
    background-color: rgba(0, 0, 0, 0.1);
    margin: 12px;
    /*box-shadow: 0 1px 4px #363636;*/
    border-radius: 0px;
    border-color: rgba(255, 255, 255, 0.1);
    border-left-width: 0px;
    border-right-width: 0px;
  }

  .card-header {
    background-color: rgba(0, 0, 0, 0.25);
    border-width: 0px;
  }
  
  em.os-author-role {
    color: #00b4ff;
    font-style: normal;
  }
}
