css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://360.cn/") {
#doc-hd {
background:url(https://s31.postimg.org/hyt7d3lvf/body_bg.jpg);background-repeat:repeat; 
}
.news-block {background:#ff0030;}
#search-block .btn-submit {background-color:#dc0030;}#search-block {border: 2px solid #ff0030;}.fast-download {background: #fff}
.news-block .box-hd .title {color:#fff;font-size: 20px;}
#news-block .headline  .item.need_to_fixed  a {color:#fff;}
.news-block .headline .desc {color: #fff;}
.headline .title  a {color:#fff;}
.focus-block {background:#fff;}
.soft-block .star-list .item:hover {background:#ff0030;}
#news-block .box-bd {background:none;}
#soft-block .box-bd {background:#fff;}
#iot-block  .mall-recommend.clearfix {background:#ffffff;}
#iot-block  .cate-list {background:#fff;}
#soft-block  .need_to_fixed a {color:#333333;}
#iot-block  .need_to_fixed a {color: #333333;}
.headline  .desc a {color:#ffffff;}
.snews-block .box .item.need_to_fixed  a {color:#333;}
#copyright {background: #ffffff; padding: 24px 0;}
#related-block  .box-bd:hover {background:#ff0030;}
#related-block .snews-block  .pic img {width:100%;}
.related-block .box-bd {padding: 6px;}
.headline .pic img {width: 100%;}
#related-block .box-bd:hover .item.need_to_fixed > a {color: #ffffff;}
#related-block .title {color: #ff0030;}
#related-block .more {color: #ff0030;}
#soft-block .title a {color: #ff0000 !important;}
#soft-block .text .title a {color: #333 !important;}
#iot-block .title a {color: #ff0000 !important;}
#iot-block .text .title a {color: #333 !important;}
#soft-block.soft-block .star-list .item:hover .text .title a {color:#fff !important;}
#soft-block.soft-block .star-list .item:hover .text .desc {color:#fff !important;}
#news-block .item.need_to_fixed  a{color:#fff;}
.focus-slide .tabs .tabitem .bg
{
background:#d8d8d8;
opacity:1;
}
.item:hover .download a
{
background:#fff !important;
}
.focus-slide .tabs .active .bg
{
opacity:1;
background: url(data:image/png;base64,);
background-repeat: no-repeat;
}
#search-form .btn-submit
{
color: #000;
}
.news-block .news-list .item
{
background:url(data:image/jpeg;base64,);
background-repeat:no-repeat;
background-position:left 12px;
line-height:25px;
}
.related-block .headline .text
{
background:rgba(0, 0, 0, 0.1);
}
#related-block .box-bd:hover .text
{
background:#ab1733;
}
.box-bd:hover .text a
{
color:#fff ;
}
.box-bd:hover .item
{
background:url(data:image/jpeg;base64,);
background-repeat:no-repeat;
background-position:left 15px;
}
.news-block .headline .desc p, .news-block .headline .desc p a
{
color:#ffd1da;
}
}
