/* 
 * 主要针对 51CTO 学院的教师个人页面、课程列表页和教师列表页进行设置：
 * 
 * 1. 隐藏无用的页面内容；
 * 2. 适当调整页面元素的位置；
 * 
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("edu.51cto.com") {
  .qqbm_bs_box{         /* 右侧在线咨询  */
    display: none;
  }
  
  .Page.Top, 
  .Page.NavTop.Navs {   /* 首页搜索和导航 */
    margin-top: 10px;
  }
  
  .Header {              /* 导航条       */
    padding: 20px 0px 20px 20px;
  }
  
  .footer_bg {              /* 页脚         */
    display: none;
  }
}
