css:
@-moz-document url-prefix("http://hd.kibank.ru/scp/") {
body,
html {
    background: #2d2d2d;
    color: #bcbcbc;
}
.type-icon {
    background-color: #1d1d1d;
    /* border: 1px solid #1d1d1d; */
}
.thread-event .type-icon::after {
    border: 16px solid #1d1d1d;
}
.dialog {
    background: #2d2d2d;
    border: 2px solid #e0b400;
}
.dialog th {
    background: #2d2d2d;
}
.thread-body {
    color: #bcbcbc;
    background-color: #2d2d2d;
}
.thread-entry::before {
    border-top: 2px solid #2d2d2d;
}
.thread-entry::after {
    border-bottom: 2px solid #2d2d2d;
}
.ticket_info {
    background: #2d2d2d;
}
.tip_content {
    background: #2d2d2d;
}
.tixTitle h3 {
    color: #bcbcbc;
}
.ticket_info.custom-data thead th {
    background-color: #2d2d2d;
}
ul.tabs.clean li.active {
    background-color: #1d1d1d;
}
.sticky.bar.fixed {
    background: #2d2d2d;
}
select {
    background: #bcbcbc;
}
.select2-container--default .select2-selection--single {
    background-color: #bcbcbc;
}
table.list {
    background-color: #2d2d2d;
}
table.list thead th {
    background-color: #2d2d2d;
    color: #bcbcbc;
    border: 1px solid #bbb;
}
element.style {
    background-color: #849984;
}
table.list tbody tr:hover td,
table.list tbody tr.highlight td {
    background: #878774;
}
table.list tbody tr:nth-child(2n+1):hover td {
    background: #878774;
}
table.dashboard-stats tbody:nth-child(2) tr:hover {
    background-color: #878774;
}
table.list thead th a.asc {
    background-color: #878774;
}
table.list thead th a.desc {
    background-color: #878774;
}
table tfoot td {
    background: #2d2d2d;
}
table.list tbody td {
    /*background: #5a5a5a; */
    
    background: #3d3d3d;
}
table.list tbody tr:nth-child(2n+1) td {
    background-color: #3d3d3d
}
#header {
    background-color: #2d2d2d;
}
#sub_nav {
    background: #2d2d2d;
}
#basic_search {
    background-color: #2d2d2d;
}
#nav .active a {
    color: #e0b400;
}
#nav .inactive a {
    color: #e0b400;
}
#nav .active,
#nav .inactive {
    color: #e0b400;
}
#nav li.inactive > ul {
    background: #2d2d2d;
}
#nav li.active {
    background-color: #2d2d2d;
}
table.list thead th a {
    color: #bcbcbc;
}
#content {
    border-bottom: 1px solid #bbb;
    background: #1d1d1d;
}
#sub_nav a {
    color: #bcbcbc;
}
.form_table th,
div.section-break {
    background: #1d1d1d;
}
#header p {
    background: #1d1d1d;
}
#nav {
    background: #2d2d2d;
}
#nav li.active {
    background-color: #2d2d2d;
}
#nav li.inactive:hover {
    background-color: #2d2d2d;
}
a {
    color: #e0b400;
}
.dialog a {
    color: #e0b400;
}
h2 {
    color: #e0b400;
}
.action-button,
.action-button.muted,
.button {
    color: #e0b400;
    box-shadow: 0 0 0 1px #e0b400 inset;
}
.dialog h3 {
    color: #e0b400;
}
.green.button:hover {
    box-shadow: 0 0 0 2px #16ab39 inset;
    color: #16ab39;
}
.button:hover,
.button:active,
.action-button:hover,
.action-button:active,
input[type=button]:hover,
input[type=button]:active,
input[type=reset]:hover,
input[type=reset]:active {
    color: #e0b400;
    box-shadow: 0 0 0 2px #e0b400 inset;
}
.quicknote a.action,
.floating-options a.action {
    color: #e0b400 !important;
}
.link.truncate {
    color: #e0b400;
}
input[type=text],
input[type=password],
textarea,
input {
    background: #bcbcbc;
}
.form_table th em {
    color: #bcbcbc;
}
.link,
a {
    color: #e0b400;
}
table {
    border-collapse: collapse;
}
ul.tabs {
    background: #1d1d1d;
}
#response_options > form {
    background: #1d1d1d;
}
ul.tabs li.active {
    color: #bcbcbc;
    background-color: #1d1d1d;
}
ul.tabs li.active a {
    color: #bcbcbc;
}
.redactor-editor,
.redactor-box {
    background: #3d3d3d;
}
.faded {
    color: #bcbcbc;
}
.faded b {
    color: #bcbcbc;
}
.faded strong {
    color: #16ab39;
}
.faded-more {
    color: #bcbcbc;
}
button[type=submit]:hover,
input[type=submit]:hover,
input[type=submit]:active {
    color: #16ab39;
    box-shadow: 0 0 0 2px #16ab39 inset;
}
.thread-body .attachments {
    background-color: #2d2d2d;
}
.priority.low {
    color: #b3ffa3;
    /* background-color: #93c3e2!important; */
    
    background-color: #3d3d3d!important;
}
.priority.normal {
    color: #aaf5ff;
    background-color: #3d3d3d!important;
}
.priority.high {
    color: #ffa3a3;
    /* background-color: #e8877d!important; */
    
    background-color: #3d3d3d!important;
}
.priority.emergency {
    color: #ee5f5b;
    background-color: #3d3d3d!important;
}
#msg_warning,
.warning-banner {
    background-color: #2d2d2d;
}
#logo > img {
    content: url('http://hd.kibank.ru/scp/images/logo.png');
}

table.dashboard-stats tbody:nth-child(2) tr:nth-child(odd) {
    background-color: #2d2d2d;
}

#kb li {   
    background-image: url(http://hd.kibank.ru/scp/images/kb_large_folder.png), url(http://hd.kibank.ru/scp/images/kb_category_bg_2d2d2d.png);    
}
}
