@-moz-document domain("192.168.1.1") {
@font-face {
  font-family: 'Open Sans';
  src: local('Open Sans'), local('OpenSans'), /* in case the font is installed */
       url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

* {font-family: "Open Sans" !important}
html {margin:0;padding:0;border:0; direction:ltr; height: 100%;}
body, h1, h2, h3, h4, h5, h6, hr, p, blockquote, dl, dt, dd, ul, ol, li, pre, form, fieldset, legend, button, input, textarea, th, td ,iframe {margin: 0;padding: 0;}
body, button, input, select, textarea, pre { font:13px Arial, Helvetica; }
h1, h2, h3, h4, h5, h6 { font-size: 100%; }
address, cite, dfn, em, var { font-style: normal; }
code, kbd, samp { font-family: courier new, courier, monospace; }
small { font-size: 13px; }
ul, ol, li { list-style: none outside none; }
sup { vertical-align: text-top; }
sub { vertical-align: text-bottom; }
legend { color: #000000; }
textarea { resize:none; }
fieldset, img { border: 0 none; }
button, input, select, textarea { font-size: 100%; }
table {border-collapse: collapse; border-spacing: 0;}
a{ text-decoration:none; color:#000;}
a:hover{ text-decoration:none; color:#000;}
body, button, input, select, textarea, pre, .main_content{
    font-family: 'Open Sans', sans-serif;
}

.main_content p {
    margin-bottom: 10px;
    font-family: 'Open Sans', sans-serif;
}


/*************************************************************random shit************************************************************************/




::selection {
  background: white; /* WebKit/Blink Browsers */
}
::-moz-selection {
  background: white; /* Gecko Browsers */
}

body{
    background-color:#181818;
}


.main_menu{
 	background:none;
}


#footer, .login_box_info, clr_blue_title_h2, clr_bold_login_box_info_table{
	background: #181818;
    padding-bottom: 2rem;
  	bottom:0;
    max-height: 0px;
}

.login_box_info, clr_blue_title_h2, clr_bold_login_box_info_table{
	min-height:250px;
}

.main_menu ul li a.active {
	background:none;
}

.dialog_close_btn, .info_dialog_close_btn, #psw_close_btn, .dialog_newversion_close_btn{
    top: 18px;
    right: 18px;
    position:static;
}

.login_dialog, .new_version_dialog, .login_dialog, .login_dialog_table, .login_dialog_header, .login_dialog_table_bottom {
	background-color: #181818;
}

.dialog *, .dialog_content, .new_version_dialog, .dialog_table, .dialog_header, .login_dialog_table_bottom {
	background-color: #181818;
}

.login_dialog, .new_version_dialog{
	padding-top: 0;
}



body #div_wrapper, .wait_div, #ifream_dialog, #div_wrapper_newVersion iframe {
    opacity: 0.9 !important;
    background:black !important;
}

.element.style {
    background: #000000;
    text-decoration: none;
}

.center_box{
	min-height: 200px;
}

.content, .login_wrapper_dim, .login_wrapper_filter{
    background-color:#181818;
    min-height: 99vh;
}



.notification{
   background:none;
}

select {
    color: black;
    background-color: #181818;
    cursor: default;
    border-width: 1px;
    border-color: #464646;
}

input {
    color: black;
    background-color: #181818;
    cursor: default;
    border-width: 1px;
    border-color: #464646;
}

.sms_btn_bg {
    width: 638px;
    height: 29px;
    padding: 0px;
    text-align: center;
    border: 0px solid #dbdbdb;
    background:none;
}

.button_dialog {
    background-image:none;
    border-radius: 2px 2px 2px 2px;
    cursor: pointer;
    float: left;
    font-weight: bold;
    height: 25px;
    overflow: hidden;
    border: 1px solid #75ACD6;
    padding: 0 15px;
    border-color:grey;
}

.disable_btn {
    background-image:none;
    border: 1px solid #B1B1B1;
    border-radius: 2px 2px 2px 2px;
    cursor: default;
    float: left;
    font-weight: bold;
    height: 25px;
    overflow: hidden;
    padding: 0 15px;
    border-color:black;
}

* {
  -webkit-border-radius: 0 !important;
     -moz-border-radius: 0 !important;
          border-radius: 0 !important;
}

.mouse_on .button_dialog{
	background-image:none;
	border: 1px solid #72C0EA;
    border-color:#e0e0e0;
}

.agile_station h2, .agile_lgtab h2 {
    border: 0px;
	border-bottom: #a6a6a6 solid 1px;
    background:none;
    text-align: center;
}

.sms_dialog_content *, .upload_dialog_content * {
    background-color: #181818;

}

