css:
@-moz-document domain("kinozal.tv"), domain("kinozal.me") {
* {outline:0;}
body, #main{background-color: #212121;color: #efefef;}
.logo_new, .logo{background: url(https://pbs.twimg.com/profile_images/517698907120209920/birornhb_400x400.png)   0 22px;background-color: #6b6b6b;background-size: 75px;min-width:75px!important;padding:0 0 0 0!important;position:fixed;top:-23px;opacity: 0.3}
.logo_new img,.logo img{opacity:0;width:75px!important;height:90px!important;min-width:0!important}
#header > table td[style="width:43%;"], #header .toper {position: fixed;z-index: 1001;width: 800px !important;left: 50%;margin-left:-400px}
#header {height: 75px;background: #333;}
/* уменьшение ограничений ширены */
body, #header, #main, #footer {min-width: 750px}
#main .content{min-width: 750px !important}
/* менюбар */
#header .menu {
    width: 100%;
    height: 75px;
    position: fixed;
    top: 0;
    box-shadow: -11px 0px 4px 3px rgba(0,0,0,0.35);
    background: #333;
    z-index: 1000;
}
/* пункты меню гуппа*/
#header .menu ul {
    position: absolute;
    top: 0;
    width: 620px;
    right: 50%;
    margin-right: -320px; 
}
/* пункты меню элемент */
#header .menu ul li a {height: 18px !important;color: #ececec}
#header .menu ul li a:hover {background: #fff}
/* форма поиска */
#header .menu form {
    background: rgba(255,255,255,0);
    width: 400px;
    height: 24px;
    border: 1px  rgba(181, 181, 181, 0.91) solid;
    border-radius: 3px;
    position: absolute;
    top: 23px;
    right: 50%;
    margin-right: -200px;
    padding: 8px 5px;
}
/* поле поиска */
#header .menu form input.inp {
    font-weight: 900;
    width: 360px;
    padding-left: 5px;
    color: #fff !important;
    height: 22px;
    border: 0;
    background: #333 !important;
}
/* кнопка поиска */
#header .menu form .s_submit {
    background: url(http://slimshop.ru/images/search_bttn.png) no-repeat scroll left top transparent;
    height: 35px;
    width: 35px;
    margin-top: -5px;
    cursor: pointer;
}
/* жолтое поля автокомплита поиска */
input {outline: none;}
input:-webkit-autofill {
    -webkit-box-shadow: inset 0 0 0 50px #333 !important; /* Цвет фона */
    -webkit-text-fill-color: #fff !important; /* цвет текста */
    color: #fff !important; /* цвет текста */
}
/*шторки, реклама*/
#header .zan_l, #header .zan_r, .mgbox/*, .pad0x0x5x0*/, .rb_new {display: none !important}
/*блок рекламы слева, тело блока на главной*/
.imgmn, .tp1_body { background: #616161}
/* точки */
.bulet {background: #8eab2f}
/* переключатель страниц */
.paginator ul li a{border: 1px solid #8eab2f!important}
/*плитки*/
.bx2_0, .bx2, .bx1_0, .mn_wrap, .tp1_border {box-shadow: 0 0 1px rgba( 0,0,0, 0.20 );;background: #616161;border-radius: 2px;border: 0;}
.mn2,.bx1, .bx5x5 {background: #616161;border: 0}
.mn1_content .bx2_0, .mn1_content .bx1_0{box-shadow: none;}
/* серая вкладка */
.mn2, .bx5x5, .tp1_title {border-top: 3px solid #2f2f2f}
/* пункты вкладки */
.bx1, .mn1_content .bx1_0 {border-left: 3px solid #8eab2f}
/* края плитки */
.bx2_0 {border: 3px solid #616161}
/* заголовок плитки, заголовок блока в меню */
ul.men li.center, .tp1_title {
    background-color: #616161;
	font-size: 13px;
    font-weight: bolder;
    color: #98c700;
}
/* ну тут все ясно */
#footer {display: none}
/* цвет ссылок в меню пользователя */
.u1, .u1 a, a.u1, a.u1:link, a.u1:active, a.u1:visited {color: #484646}
/* чередование цвета для строк */
tr:nth-of-type(2n) {background: #717171}
.men li.tp, .mn {background-color: #828181!important}
/* основной контент */
#main .content > div {width: 99%}
@media only screen and (min-width: 970px) {
    #main .content > div {width:750px;margin-right:-350px;}
    #main .content > form {width: 755px;}
    #main .content > table {width: 755px;}
    #main {position: absolute;left: 50%;margin-left:-480px;}
}
/* правое меню */
#main .content > .mn2_menu {width: 190px;margin-right: 0;}
/* панелька радио */
.w100p {width: auto}
/* кноака загрузка торрента */
.mn1_content td a[href] img {-webkit-filter: hue-rotate(30deg);filter: hue-rotate(30deg);transition: 0.5s;}
.mn1_content td a[href] img:hover {-webkit-filter: hue-rotate(0deg);filter: hue-rotate(0deg)}
/* кнопка загрузки на главной странице */
.tp1_but {-webkit-filter: hue-rotate(30deg);filter: hue-rotate(30deg);transition: 0.5s;}
.tp1_but:hover {-webkit-filter: hue-rotate(0deg);filter: hue-rotate(0deg)}
a.r1,a.r2,a.r3,a.r5{display: block;box-shadow: 1px 1px 1px 0px #848383;border-radius: 1px}
a.r0 {font-weight: 100!important;color: #fdfdfd!important;}/* раздача */
a.r1 {font-weight: 100!important;color: #fdfdfd!important;background-color: rgba(255, 197, 0, 0.39)}/* золотая раздача */
a.r2 {font-weight: 100!important;color: #fdfdfd!important;background-color: rgba(226, 226, 226, 0.39)}/* серебр. раздача */
a.r3 {font-weight: 100!important;color: #fdfdfd!important;background-color: rgba(158, 37, 37, 0.39)}/* тест. раздача */
a.r5 {font-weight: 100!important;color: #fdfdfd!important;background-color: rgba(230, 4, 4, 0.39)}/* CAMP/TS. раздача */

.u1, .u1 a, a.u1, a.u1:link, a.u1:active, a.u1:visited {color: #dedede}
.sba, a.sba, a.sba:link, a.sba:active, a.sba:visited {color: #91afff}
a:link, a:visited {color: #c7c7c7}
.sbab, a.sbab, a.sbab:link, a.sbab:active, a.sbab:visited {color: #a4bcff}
.u9, .u9 a, a.u9, a.u9:link, a.u9:active, a.u9:visited {color: #ff8383}
.paginator ul li:hover a {background-color: #464646;color: #f91c51}
.paginator ul li a {background-color: #4e4e4e}
.paginator ul li.current a {background-color: #3e3e3e}
.u5, .u5 a, a.u5, a.u5:link, a.u5:active, a.u5:visited {color: #2424ff}
b {color: #f7f7f7}

.t_peer td.sl_s {color: #07ec07}
.prs a:link, .prs a:visited {color:#2d0808}
.green, .green:link, a.green {color: #3be03b}
.w100p {
    background: #616161!important;
}
font[color="green"] {
    color: #97ea97;
}
}
