css:
@-moz-document regexp("^https?://kissanime[.]ru.*$") {
@font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
    }
    html, body {
        background: none !important;
        /*[[colour]]*/
        height: initial !important;
        font-family: 'Roboto' !important;
    }
    body > #containerRoot {
        min-height: initial !important;
    }
    .divCloseBut {
        display: none !important;
    }
    .bigBarContainer .barTitle {
        display: none;
    }
    .arrow-general {
        display: none;
    }
    .bigBarContainer {
        background: none;
        border: none;
        margin-bottom: initial;
        padding: initial;
        background-color: rgba(17, 17, 17, 0.6);
    }
    .barContent {
        background: none;
        padding: 12px;
    }
    #spanBookmark {
        display: none;
    }
    div#leftside > div:nth-of-type(2) > div.barContent.episodeList > div:nth-child(2) > div:nth-child(-n+6) {
        display: none !important;
    }
    .listing th {
        border-bottom: 1px solid #999999;
    }
    #containerRoot > div:nth-child(12),
    #containerRoot > div:nth-child(14) {
        display: none !important;
    }
    #fbComments {
        display: none;
    }
    .dotUnder {
        border: none;
    }
    #footer {
        top: initial !important;
        background: none;
        height: initial;
        padding: 20px;
    }
    #footcontainer {
        text-align: center;
    }
    #footcontainer p {
        margin: initial;
    }
    #head {
        padding-left: 40px;
        width: initial;
    }
    #containerRoot > div:nth-child(11) {
        height: initial !important;
    }
    .banner {
        border: none;
        background: none;
        background-color: rgba(17, 17, 17, 0.6);
    }
    .details a img {
        border-color: transparent;
        padding: initial;
    }
    .details a img:hover {
        border-/*[[a-colour]]*/;
    }
    div.items img {
        border-color: transparent;
    }
    div.items img:hover {
        border-/*[[a-colour]]*/;
    }
    #subcontent {
        border: none;
        background: none;
        background-color: rgba(17, 17, 17, 0.6);
    }
    #subcontent div div {
        width: 98%;
        background: none;
    }
    #subcontent div div.blue {
        width: 98%;
        background: none;
    }
    #tab-newest div:nth-child(-n+10) div:nth-child(5) {
        display: none;
    }
    #tab-trending div:nth-child(-n+10) div:nth-child(5) {
        display: none;
    }
    #tab-mostview div:nth-child(-n+10) div:nth-child(5) {
        display: none;
    }
    .scrollable {
        overflow-y: initial;
        height: 240px;
    }
    input.text {
        border: none;
        background: none;
        background-color: #333333;
        margin-right: 6px;
    }
    #search input.text {
        padding: 4px;
        width: 83% !important;
    }
    #result_box {
        background-color: #333333;
        padding: 5px;
        border-bottom-right-radius: initial;
        border-bottom-left-radius: initial;
        width: 75%;
        left: 56px !important;
    }
    #result_box a:hover {
        padding: 0px 5px;
        margin-left: -5px;
    }
    #formSearch div:nth-child(3) {
        width: initial !important;
    }
    #formSearch div:first-of-type a:nth-child(n+21) {
        display: none;
    }
    input#imgSearch.button {
        background-color: #B1CA05 !important;
        display: none !important;
    }
    #subcontent div a img {
        padding: initial;
        border-color: transparent;
    }
    #subcontent div a img:hover {
        padding: initial;
        border-color: #A3E9A4;
    }
    .tooltip {
        background: none !important;
        background-color: rgba(17, 17, 17, 0.9) !important;
        height: auto !important;
        width: auto !important;
        padding: 10px !important;
        font-size: 13px !important;
    }
    .tooltip div:first-of-type {
        width: 400px !important;
    }
    #navsubbar {
        background: none;
    }
    #navcontainer li a#currentTab {
        background: none;
        background-/*[[a-colour]]*/;
    }
    #navcontainer a:link, #navcontainer a:visited {
        background: none;
        background-color: #BDBDBD;
    }
    #navcontainer a:hover {
        background: none;
        background-color: #EEEEEE;
    }
    div.pagination {
        margin: initial;
    }
    div.pagination ul.pager li {
        margin: 0px 0px 0px 1px;
        background: none;
        background-color: white;
        border-color: transparent;
    }
    div.pagination ul.pager li a {
        padding: 4px 8px;
    }
    div.pagination ul.pager li a:hover {
        background: none;
        background-color: #999999;
        padding: 4px 8px;
        border-color: transparent;
    }
    div.pagination ul.pager li.current {
        background: none;
        background-color: #999999;
        padding: 4px 8px;
        border-color: transparent;
    }
    #formSearch div:nth-of-type(3) a img {
        display: none;
    }
    #topHolderBox {
        background: none;
        padding-top: initial;
        width: initial !important;
    }
    #topHolderBox img {
        display: none;
    }
    .bigChar {
        /*[[a-colour]]*/;
        letter-spacing: normal;
    }
    a {
        /*[[a-colour]]*/;
    }
    a:hover {
        /*[[a-colour]]*/;
    }
    #tabmenucontainer li a.tabactive {
        color: #FFF;
        background: none;
        background-/*[[a-colour]]*/;
    }
    #tabmenucontainer a:link, #tabmenucontainer a:visited {
        color: #1C1C1C;
        background: none;
        background-color: #BDBDBD;
    }
    #tabmenucontainer a:hover {
        color: #1C1C1C;
        background: none;
        background-color: #EEEEEE;
    }
    #tabmenucontainer li {
        text-indent: unset;
    }
    #tabmenucontainer ul {
        /*font-weight: bold;*/
    }

    /*font change start*/
    h1, h2, h3, h4 {
        font-family: 'Roboto' !important;
    }
    .details {
        font-family: 'Roboto';
    }
    .bigChar {
        font-family: 'Roboto';
    }
    #navcontainer ul {
        font-family: 'Roboto';
    }
    #tabmenucontainer ul {
        font-family: 'Roboto';
    }
    #subcontent div div {
        font-family: 'Roboto';
    }
    .title {
        font-family: 'Roboto';
    }
    #subcontent span.title {
        font-family: 'Roboto';
    }
    #topHolderBox {
        font-family: 'Roboto';
    }
    input.text {
        font-family: 'Roboto';
    }
    #result_box {
        font-family: 'Roboto';
    }
    /*font change end*/

    #result_box a {
        line-height: normal;
    }
    .listing tbody tr td a:hover {
        /*[[a-colour]]*/;
    }

    #nextEpisodeCountDown {
        /*[[a-colour]]*/ !important;
    }
    .episodeList td a:visited {
        /*[[a-colour]]*/;
    }
    .specialButton {
        border-radius: 0px !important;
        box-shadow: none !important;
        text-shadow: none !important;
        background: none !important;
        font-family: 'Roboto' !important;
        background-/*[[a-colour]]*/ !important;
        border: none !important;
    }
    .specialButton:hover {
        background-/*[[a-colour]]*/ !important;
    }
    #containerRoot > div:nth-child(20) {
        background-color: #212121 !important;
    }
    select {
        border: 1px solid #393939;
    }
    button#playerChoose {
        border-radius: 0px !important;
        text-shadow: none;
        background-/*[[a-colour]]*/;
    }
    button#playerChoose:hover {
        background-/*[[a-colour]]*/;
    }
    .barTitle {
        background: none;
        font-family: 'Roboto';
        letter-spacing: normal;
        padding-right: 10px;
        padding-left: 13px;
        background-/*[[a-colour]]*/;
    }
    .scrollable_title {
        letter-spacing: normal;
    }
    a.prev, a.prev:hover {
        background: url(https://i.imgur.com/hrwRsig.png);
    }
    a.next, a.next:hover {
        background: url(https://i.imgur.com/xaR51zB.png);
    }
    #navbar {
        background: none;
    }
    #navsubbar p {
        background-/*[[a-colour]]*/                                                                                   ;
    }
    div.navi {
        margin-right: 10px;
    }
    #divAds, #divAds2 {
        display: none;
    }
    .alphabet {
        background: none !important;
    }
    #footcontainer p a:hover {
        color: #A3E9A4;
    }
    .barContent > div:only-child > div:nth-child(11) {
        border: none !important;
        background: none !important;
    }
    #divComments > div:first-child > div:nth-child(2),
    #leftside > div.bigBarContainer:nth-child(7) > div.barContent.episodeList > div:nth-child(2) > div:first-child {
        color: #D32F2F !important;
    }

    #rightside {
    display: none !important;
}
#leftside {
    width: 100%;
}
.items div {
    width: 675px;
}
div.items a {
    margin: initial !important;
}
.scrollable .items div :nth-child(5) {
    margin-top: -18px !important;
}

    /* ad/div hider start */
    iframe#adsIfrme1,
    iframe#adsIfrme6,
    iframe#adsIfrme7,
    iframe#adsIfrme8,
    iframe#adsIfrme9,
    iframe#adsIfrme10 {
        visibility: hidden;
        height: 0;
    }
    .barContent > div:only-child > div:nth-child(2),
    .barContent > div:only-child > div:nth-child(3),
    .barContent > div:only-child > div:nth-child(8),
    .barContent > div:only-child > div:nth-child(9),
    .barContent > div:only-child > div:nth-child(17),
    .barContent > div:only-child > div:nth-child(20),
    .barContent > div:only-child > div:nth-child(22),
    .barContent > div:only-child > div:nth-child(23) {
        display: none !important;
    }
    /* ad/div hider end */
}

@-moz-document regexp("^https?://kissanime[.]ru/$") {
.bigBarContainer .barTitle {
        display: block;
    }
}

@-moz-document regexp("^https?://disqus[.]com/embed/comments/.*$") {
@font-face {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 400;
        src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
    }
    html, body {
        font-family: 'Roboto' !important;
    }
}

/*[[ka-colour]]*/
