css:
@-moz-document domain("forums.overclockers.co.uk") {
.pageWidth {
    margin: 0px;
}
.messageUserInfo {
    margin-right: 0px;
    border-right: none;
    display: flex;
    width: 158px !important;
}
.messageList .message {
    padding: 0px 0px 0px 5px!important;
    min-height: 260px;
    clear: both;
}
.postDate {
    padding: 0px;
    height: 3px;
}
.postDate a {
    position: relative;
    top: 230px;
    left: 0px;
}
.postDate ol {
    display: none;
}
#content .pageContent {
    padding: 0px 5px;
}
.messageUserInfo {
    width: 150px;
}
.discussionListItems {
    border-left: 1px solid black;
    border-right: 1px solid black;
}
.forumLink {
    font-weight: 600;
}
.quickReply {
    zoom: 1;
    background: rgb(0, 51, 102);
    padding: 5px;
    border: 1px solid black;
}
.redactor_box {
    padding-left: 10px;
}
.PageNav a {
    font-size: 120%;
    margin-right: 5px;
}
.PageNav .scrollable {
    width: auto;
}
.PageNav .scrollable .items {
    width: auto;
    position: relative;
}
.messageInfo {
    margin-right: -6px;
}
.postDate:first-of-type {
    height: 0px;
}
.editDate {
    margin-right: 10px;
}
.message .privateControls {
    float: right;
    margin-right: 15px;
}
#Offer {
    display: none;
}
#logo {
    float: none;
    text-align: center;
    height: 100px;
    margin-top: -10px;
}
#headerMover #headerProxy {
    height: 136px;
}
.footer {
    display: none;
}
.sidebar .section {
    border-bottom: 0px;
}
.footerLegal .pageContent {
    padding: 0px;
}
#legal {
    float: none;
}
#legal li {
    float: none;
    margin-left: 0px;
}
.sharePage {
    display: none;
}
#pageDescription {
    margin-left: 15px;
}
.navTabs {
    border-top: 0px;
    border-right: 0px;
    border-left: 0px;
    margin-left: 5px;
    margin-right: 5px;
}
.breadBoxTop {
    padding-top: 6px;
    padding-bottom: 3px;
}
.tabLinks {
    margin-left: 5px;
    margin-right: 5px;
    width: auto !important;
    right: 0px;
}
.UserRankImages li {
    border: 0px solid #fff;
    width: 85px;
    height: 85px;
    margin: 0 auto;
}
.UserRankImages {
    display: block;
}
.messageUserBlock {
    margin-left: auto;
    margin-right: auto;
    text-align: center;
}
}
