css:
@-moz-document domain("platzi.com") {
.File .render {
	background: #2a2a2a
}
.hljs-keyword,
.hljs-literal,
.hljs-name,
.hljs-selector-tag,
.hljs-strong,
.hljs-tag {
	color: #f92672;
	font-weight: 700
}
.Comment {
	border-radius: 5px;
	padding: 10px;
	box-shadow: 0 1px 4px 0 rgba(0, 0, 0, .14);
	background: #fff
}
#video-content>div>div.Live-chat.VideoMaterialLayout-tabs>section>div.TabSystem-panels>div>section>section>article>div.Comment-wrapper>div.Comment-body>section>article {
	border: none;
	background: #f7f7f7;
	border-radius: 2px
}
.CommentActions {
	background-color: #0791e6;
	border-radius: 3px;
	border: 1px solid #0791e6
}
.Comment-wrapper:after,
.Comment-wrapper:before {
	display: none
}
.CommentActions-reply {
	padding: 1px;
	color: #fff!important
}
.CommentStar {
	border: none;
	padding-top: 5px;
	padding-bottom: 5px;
	background: 0 0
}
.CommentStar-icon {
	color: #ccc;
	text-shadow: none
}
.CommentContent-text {
	margin-bottom: 1rem
}
.CommentList:not(.isNested)>.Comment:before {
	top: 80px;
	bottom: 1.5rem;
	left: 28px
}
.CommentList:not(.isNested)>.Comment:after {
	left: 28px;
	bottom: 1.5rem
}
.Tab .counter {
	background: #f92672;
	font-size: 12px;
	border-radius: 100%;
	padding: 2px 5px
}
.MaterialTabs-content {
	background: 0 0
}
.TabSystem-tabs [type=radio]:checked+.Tab {
	background: #f3f3f6;
	border-radius: 5px 5px 0 0;
	border-top: 1px solid #ddd;
	border-left: 1px solid #ddd;
	border-right: 1px solid #ddd;
	box-shadow: none
}
.TabSystem-panels {
	background: #f3f3f6
}
.TabSystem-top {
	background: #1c3643;
	padding-top: 2px
}
.Tab {
	margin: 0 .5rem
}
.is-discussions label:nth-child(2) {
	background: #f3f3f6!important;
	color: #1c3643!important;
	border: none!important
}
.is-files label:nth-child(2) {
	background: 0 0!important;
	color: #fff!important;
	border: none!important
}
.is-files label:nth-child(4) {
	background: #f3f3f6!important;
	border: none!important
}
.is-discussions label:nth-child(4) {
	background: 0 0!important;
	border: none!important
}
.VideoMaterialLayout-tabs {
	margin-top: 0
}
#video-content>div>div.Live-primary>div.VideoMaterialLayout-below>section>div.TabSystem-tabs>div.TabSytem-group>label {
	background: #fff;
	border: none
}

.VideoMaterialLayout-below {
	padding-left: 3rem;
}
.InitialComment {
	background: #fff;
	border: 1px solid #ddd
}
.Information {
	margin: 0;
	margin-top: .5rem;
}
.Chat-filter {
	border-radius: 2px;
	background: #fff;
	color: #1c3643
}
.Chat-filter.is-active {
	background: #0791e6;
	color: #fff!important
}
.TimelineNav {
	top: -52px;
}
.TimelineNav-materials {
	margin-top: 1rem;
	display: none
}
.TimelineNav-button {
	background-color: #1c3643!important;
	width: 100%;
	height: 52px
}
.TimelineNav-unfolded {
	background-color: #f3f3f6!important;
	width: 48px!important;
	max-width: 48px
}
.icon-arrow-right_A {
	background: url(https://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_menu_white_18dp.png) center/60% no-repeat
}
.icon-arrow-right_A:before {
	content: ""
}
.TimelineNav-folded {
	height: calc(100vh + 3.5rem) !important
}
.TimelineNav-unfolded {
	background: 0 0!important
}
.TimelineNav-unfolded.shadow {
	box-shadow: none
}
.TimelineNav-folded::-webkit-scrollbar {
	width: 7px!important
}
.TimelineNav-folded::-webkit-scrollbar-thumb {
	background-color: #0a8ecd!important
}
.TimelineNav-folded::-webkit-scrollbar-corner {
	background-color: transparent
}
.TimelineNav-folded::-webkit-scrollbar-button {
	background-color: transparent;
}
.TimelineNav-folded::-webkit-scrollbar-track {
	background-color: transparent;
}
.VideoMaterialLayout-player {
	margin-left: 3rem;
	margin-top: 1rem;
}
#video-content>div>div.Live-primary>header>figure {
	margin-left: 3rem;
}
.icon-arrow-down {
	color: #fff!important;
}
.MenuOption-dropdown {
	border: none!important;
}
}
