css:
@-moz-document domain("wykop.pl") {
#bmone2n-7069\.1\.1\.30, #bmone2n-7069\.4\.1\.36, #bmone2n-7069\.2\.1\.36, #bmone2n-7069\.3\.1\.36 {
display:none!important;
}
#bmone2n-7069\.1\.1\.36, #bmone2n-7069\.4\.1\.30, #bmone2n-7069\.2\.1\.30, #bmone2n-7069\.3\.1\.30 {
display:none!important;
}
.grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide {
display:none!important;
}
.grid-main {
margin-right: 0px
}
.grid, #site > div.wrapper
{padding: 0px}
.socialbar {
display:none!important;
}
.wrapper{width:90%!important;}
.grid-full
{padding-left:0px}
#dyingLinksBox {
display:none!important;
}
body, body *
{font-family: "Verdana"}
body
{background:#475c4d}
#new-frame
{background:#3d4f42}
.mark-bg
{background:#cfd7ca!important}
h4
{border-bottom: 1px solid #3d4f42}
::selection
{background: #328efe;
color: #fff}
#footer h4
{border-bottom: 1px solid #3d4f42}
#footer h4, #footer a
{color: #a9b1a6}
#footer a
{color: #a9b1a6}
#footer a.tag:hover
{background-color: #508fa3}
#footer
{background: #506256}
.button
{color:#506256;
background: #cad4c7;
font-size: 11px;
font-weight: normal}
#nav .nav li.active a
{background-color: #313836}
#nav
{background: #3d4f42}
em.mark-number
{background: #475c4d}
#nav .nav li a
{color: #adc0b3;
font-size: 12px}
#nav .nav li a:hover, #nav .nav li.active a
{color: #fff!important;
border-color: #313836!important;opacity: 1}
#nav li.notification p a:hover, .notification p a:hover
{color: #000 !important;}
#nav .notification a b, .notification a b
{box-shadow: 0 1px #600;
background: #f7590a;
text-shadow: 0 1px #900;
color: #fff;
border-radius: 3px;
text-align: center;
font-size: 11px}
.nav > ul li a
{font-size: 11px;
color: #48524f}
.diggbox span:first-child, .rdiggbox
{color:#cbe0b6;
font-size: 12px!important;
background: #4383af;
border-radius: 4px}
.diggbox.digout span:first-child
{color:#658b4b;
font-size: 12px!important;
background: #cad4c7}
.diggbox a.dropdown-show
{color:#4383af;
font-size: 10px!important}
.diggbox span.button
{color:#4383af;
font-size: 10px!important;
border-radius: 2px;
border:0px}
.article h2
{font-size: 14px!important}
a.link, a
{text-decoration: none;
color: #2c3635}
body a:hover
{color: #4f615f !important}
.article h2 a:visited
{color: #81938e}
.create.tag:nth-of-type(1) > .affect
{color: #468ddc!important;
font-weight: bold;
font-style:normal!important}
.article > div p.text
{font-size: 13px!important}
.article > div p.text a
{color: #2c3635}
.article > div p.text a:hover
{color: #4f615f!important;
border-bottom: 0px!important}
.lcontrast .affect
{color: #a8b1a6 !important}
.dC.preview.clearfix.article > .m-reset-margin.lcontrast > .elements.row > .affect:nth-of-type(1)
{font-size: 11px}
ul.related li > div div.content span
{background:#c3cdbf;}
.text p
{color: #22213a!important;
font-size: 13px!important}
.voters-list *
{color: #000 !important}
.showSpoiler, .show-more
{color:#fff!important;
background:#4fb0d1}
.media-content.too-long-pic .expand
{color:#a9b1a6!important;
background:#313836;
text-shadow:0px 0px}
.media-content .description
{background:#adc0b3;
border-top:0px;
font-size: 10px!important}
.icon-media.play, .icon-media.gif
{color: #fff!important}
.wblock textarea, .wblock form textarea
{background:#ededed;
font-size: 13px;
border:0px;
border-radius:7px}
#site
{background: #475c4d}
.comments-stream
{background: #cfd7ca}
.rbl-block
{background: #dbe3d5;
border-left:0px;
border-right:0px;
border-bottom-right-radius:0px;
border-bottom-left-radius:0px}
.comments-stream li
{background: #dbe3d5}
.author
{border-bottom:0px}
#site > div.wrapper
{background: #475c4d}
.grid
{width: 100%;
float: left;
background-color: #64816c}
}
