css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.meneame.net") {

    html, body {
        background-image: url(data:image/png;base64,) !important;

        background-repeat: repeat !important;
        background-color: #181818 !important;
    }

    .header-top-wrapper {

        background-color: #e35614;

        background-image: url(data:image/png;base64,) !important;
        background-blend-mode: difference;
    }

    body {

        color: darkgray;
    }

    a {

        color: #e35614;
    }
    #wrap {
        background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
    }
    .sidebox .votes, .sidebox .clics {
        background-color: transparent;
    }
    .header-menu-wrapper {
        background-color: #444;
        border-bottom: 1px solid #444;
    }
    .sidebox .body {
        border-bottom: 1px solid black;
        border-left: 1px solid black;
        border-right: 1px solid black;
        border-bottom-left-radius: 8px;
        border-bottom-right-radius: 8px;
        padding-top: 10px;
    }

    .sidebox .cell {
        border-bottom: 1px solid black;

    }
    .sidebox.brown .header {
        background: #202020;
    }
    .sidebox .header {
        background: #202020;
        border-right: 1px solid black;
        border-top: 1px solid black;
        border-left: 1px solid black;
    }

    .news-body h1 a, .news-body h2 a {
        color: lightgray;
    }
    .news-content {
        color: darkgray;
    }

    .news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
        border-color: #444;
        background-color: black;
    }
    .news-details a.comments {
        background-color: transparent;
    }
    .news-details , .news-details .news-details-main{

        color: darkorange;
        border-top: none !important;
        border-bottom:  none !important;

    }

    button.link.favorite {
        border-right: none;
    }

    .news-details .tool {

        border-left: none;

    }
    .news-details button.social-share {
        border-right: none;
    }

    .news-details form {

        border-left: none;
    }

    .news-body .box {

        border-color: #333;
        background: #444;

    }

    .header-menu01 ul li a {
        color: lightgray;
    }
    a.menu-more-button {
        color: lightgray;
    }

    .dropdown-menu {

        background-color: #555;
    }

    .header-menu01 a.submit_new_post {
        text-transform: uppercase;
        padding: 4px 10px;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
        border-radius: 3px;
        color: #e35614;
        margin-top: -6px;
        font-size: 12px;
        font-weight: 400;
        background-color: transparent;
        background-image: none;
        border: 0;
        box-shadow: none;
    }

    select.options-comments, select {
        background: #222;
        color: darkgray;
        border: none;
    }
    .form-control , input, textarea {
        background: #222;
        border: 1px solid black;
        color: gray;
    }


    legend, legend a, legend a:visited {
        border:none; 
    }
    .news-shakeit .votes {

        border: none;

    }
    .story-blog .main-content .text {
        font-family: 'Open Sans', 'Helvetica Neue', Helvetica, Arial;
        font-size: 16px;
        letter-spacing: 0;
        line-height: 22px;
        text-align: justify;
        color: #666;
    }
    .news-summary .warn, .story-blog .warn {

        background-color: #333;
    }
    .news-shakeit .votes, .news-shakeit .clics {
        background-color: #333;
    }

    .news-shakeit .clics {

        border: none;
    }
    .news-shakeit .votes, .news-shakeit .clics {
        background-color: #333;
    }
    #toplink, .sidebox-sub, .sneaker-legend, .pages a , .pages span,.topbox , .comment-body , fieldset {
        border: 1px solid rgba(0,0,0,.7) !important;
        border-top-color: rgba(255,255,255,.12) !important;
        border-radius: 3px !important;
        background: #333;
        box-shadow: 0px 0px 4px 0px #000 !important;

        color:gray;
    }
    #toplink{
        background-color: #432;
    }
    #toplink p {
        color: gray;
    }
    .story-blog .main-content h1 a, .story-blog .main-content h1 a:visited, #toplink h1 a {
        color: darkorange ;

    }
    .story-blog .best-comments .item .author a, .story-blog .related .item .author a, .story-blog .best-comments .item .text .tooltip, .story-blog .best-comments .item .text {

        color: #555;

    }

    .genericform {

        background: transparent;

    }



    .sidebox-rounded .body {
        border-bottom: 1px solid black;
        border-left: 1px solid black;
        border-right: 1px solid black;
    }

    .sidebox-rounded div.finished {

        background-color: black;
    }
    .sneaker-item {
        border-bottom-color: #444;
    }
    .comment-text a, a, a:focus, a:hover, a:visited, .comment-info a  {

        color: darkorange;
    }     
    .sneaker {

        background: none;
    }
    .favorite {
        display: inline-block;
        border-radius: 0px;
        border: none;
        margin: 0 4px;
        cursor: pointer;
        height: 18px;
        width: 16px;
        background: #111 url(https://mnmstatic.net/v_95/img/common/icons-common.png) no-repeat;
        background-size: 20px 300px;
        background-position: 0px -91px;
    }

    .comment-body.author, .comment.author .comment-body {
        background-color: #444;
    }

    .comment-body.phantom {

        color: #444;
        background: #222;
    }

    .threader {
        border-left: none;

    }

    .pages span.nextprev {
        color: black;
    }

    #footcol1 {

        background: none;

    }
    #footcol1, #footcol2, #footcol3, #footcol4, #footcol5 {
        border-left:  none;
    }

    .sidebox .comments li {
        border-top: none;
    }

    .sidebox .mainsites ul li {
        display: inline-block;
        color: darkgray;
        border: none;
        padding: 0px;
        margin-bottom: 2px;
        -webkit-border-radius: none;
        -moz-border-radius: none;
        border-radius: 0px;
        width: 100%;
    }

    .news-shakeit.mnm-discarded .votes, .news-shakeit.mnm-discarded .clics {
        background-color: #555;
    }

    .comment-body.high {
        background: #432 url(https://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
    }
 .comment-footer, .comment-footer a, .comment-footer span {
    color: #ab6005;

}   
    
    .comment-footer .separator-vertical {
    width: 0;
    height: 0;
    margin: 0;
    background-color: black;
}
    .comment-text a {
color:darkorange;
}
.comment.high.author .comment-body {
background: #542 url(http://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
background-blend-mode:overlay;
}
.comment-body.high.author {
background: #542 url(http://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
background-blend-mode: overlay;
}
.comment.author .comment-body {
background: #432 url(http://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
background-blend-mode: overlay;
}
.comment-body.high {
background: #432 url(http://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
background-blend-mode: overlay;
}
.comment.high .comment-body {
background: #432 url(http://mnmstatic.net/v_95/img/common/comment-high-01.png) 99% 5px no-repeat;
background-blend-mode: overlay;
}
.comment.phantom .comment-body {
background: transparent;
} 
}
