css:
@-moz-document url-prefix("https://www.pinterest.com/") {
/* general styling */

* {
	cursor:url('http://24.media.tumblr.com/tumblr_mdig6jktic1riysloo1_100.png'), auto;
}


::-webkit-scrollbar {
    width:11px;
    height:11px;
    background:#f2f2f2;
}

::-webkit-scrollbar-thumb {
    background:rgba(170,170,170,0.5);
}

::-webkit-scrollbar,::-webkit-scrollbar-thumb {
    border:5px solid #ffffff;
}

body {
    font-weight: normal!important;
}

body,p,pre,em,input {
    font-family: 'Karla', sans-serif;
    letter-spacing: 1px;
    word-spacing: 2px;
    text-transform: uppercase!important;
}



/* profile page */

.displayHeadingName, h3 {
    margin-left: 10px;
    font-family: 'Oswald', sans-serif!important;
    font-weight: 700;
    font-size: 90px !important;
    letter-spacing: 2px!important;
    color: #ddd!important;
    max-width: 440px!important;
    line-height: 110%;
}

.userLocationDescription, .antialiased, .text-md, .grey, .text-left {
    font-size: 11px!important;
    font-weight: bold;
    font-family: 'Karla', sans-serif;
    letter-spacing: .2px;
    word-spacing: 2px;
    text-transform: uppercase!important;
}

.profileBoardCover {
    width: 200px!important;
    height: 250px!important;
    padding: 15px!important;
    border-radius: 0px!important;
}

.rounded {
    border-radius: 0px!important;
    max-width: 200px!important;
}

.mt1, .info {
    width: 200px!important;
}

.py3 {
    width: 200px!important;
    padding: 15px!important;
}

.createRep {
    width: 200px!important;
    height: 200px!important;
}

.px1.py2 {
    z-index: 999999999999999999!important;                                      
}

.imageWash, img {
    width: 100px;
}

.publicBoardsWrapper, .privateBoardsWrapper, .profileGrid3col, .mb4 {
    width: calc(100% - 16%)!important;
    margin-left: auto!important;
    margin-right: auto!important;
    margin-top: 20px!important;
}


/* board headers */

.boardName, h1 {
    font-family: 'Oswald', sans-serif!important;
    font-weight: 700;
    font-size: 30px !important;
    letter-spacing: 2px!important;
}

.smallBoardName, h1 {
    font-family: 'Oswald', sans-serif!important;
    font-weight: 700;
    font-size: 20px!important;
    letter-spacing: 1px!important;
}

.description {
        font-size: 12px!important;
}

.bold-value, .pinCount, p {
    display: inline-block;
    font-size: 12px!important;
    padding: 4px!important;
}

.bold-value, .followerCount, p {
    display: inline-block;
    font-size: 12px!important;
    padding: 4px!important;
}

.addPinRep, .ModalTrigger {
    border:1px solid #d6d6d6!important;
    width: 222px!important;
    height: 300px!important;
    padding: 14px!important;
    border-radius: 0px!important;
    margin-top: -10px!important;
    margin-left: -11px!important;
}


/* pins */

.Grid {
    margin-top: 30px;
    margin-left: auto;
    margin-right: auto;
}

.pinWrapper {
    border-radius: 0px!important;
    background:#fff;
    padding:15px;
    border:1px solid #d6d6d6;
    box-shadow:rgba(0,0,0,.06) 7px 7px; 
    width: 200px!important;
}

.pinImage, .hoverMask, .Image img {
    border-radius: 0px!important;
}

.pinCredits, .CreditItem, .pinAttribution, .pinDescription {
    display: none!important;
}

.richPinGridTitle {
    display: none!important;
}

.richPinGridAttributionTitle {
    display: none!important;
}

.storyWrapper {
    visibility: hidden;
    height: 0px!important;
    width: 0px!important;
}

}

@-moz-document url-prefix("https://www.pinterest.com/pin/") {
.detailed .pinWrapper {
    min-width: 612px!important;
    max-width: 612px!important;
}

.detailed .pinImage.rounded {
    margin-left: auto!important;
    margin-right: auto!important;
    min-width: 100%!important;
    max-width: 564px!important;
}

}

@-moz-document url-prefix("https://www.pinterest.com/search/boards/") {
.User.fullname, h3 {
    font-size: 10px!important;
    margin-left: 0px;
    font-family: 'Karla', sans-serif!important;
    font-weight: normal!important;
    color: rgba(170,170,170,0.9)!important;
    letter-spacing: .5px!important;
    word-spacing: 2px;
    text-transform: uppercase!important;
}

.Board, .BoardCoverImage, .boardLinkWrapper, ul {
    max-width: 200px!important;
}
}

@-moz-document url-prefix("https://www.pinterest.com/search/people/") {
.username, h3 {
    width: 200px;
    font-size: 12px!important;
    margin-left: 0px;
    font-family: 'Karla', sans-serif!important;
    font-weight: normal!important;
    color: rgba(170,170,170,0.9)!important;
    letter-spacing: .5px!important;
    word-spacing: 2px;
    text-transform: uppercase!important;
}

.userThumbs, .userFocusImage, img {
    max-width: 200px;
    max-height: 200px;
    background-color: #fff!important;
    border-radius: 50%!important;
}
}
