css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nvd.nist.gov") {

    html {
        background-color: #222222;
        color: #dddddd;
    }

    body {
        background-color: #222222;
        color: #dddddd;
    }

    strong {
        color: #dddddd;
    }

    span {
        color: #dddddd !important;
    }

    a:link {
        color: #dddddd;
    }

    a:visited {
        color: #dddddd;
    }

    a:hover {
        color: #bbbbbb;
    }

    a:active {
        color: #dddddd;
    }

    .fdccHomeTitle {
       color: #dddddd;
    }

    #container {
        background-color: #181818;
        color: #dddddd;
    }

    .row {
        background-color: #181818;
        color: #bbbbbb;
    }

    .label {
        background-color: #181818;
        color: #dddddd;
    }

    #header {
        background-color: #2e2e2e;
        color: #dddddd;
    }

    img {
        -webkit-filter: grayscale(100%); /* For Webkit browsers */
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); /* Firefox 10+, Firefox on Android */
    }

    #contents {
        background-color: #181818;
        color: #dddddd;
        border-left: 180px solid #181818;
        border-left-color: rgb(24, 24, 24);
    }

    #contents div.searchResults dt, #contents div.searchResults dt a, #contents div.vuln-detail h3 {
        color: #dddddd;
        background-color: #383838;
    }

    #contents a, #contents .row, #contents .checkrow {
        color: #dddddd;
    }

    #contents div.searchResults dt a:hover {
        color: #dddddd;
        background-color: #383838;
    }

    #contents p.warningNote {
        color: #dddddd;
        background-color: #383838;
    }

    #contents p.warningNote a {
        color: #dddddd;
        background-color: #686868;
    }
    #contents div.searchResults dl {
        border: 1px solid #000154;
        border-top-width: 0px;
        border-right-width: 0px;
        border-bottom-width: 0px;
        border-left-width: 0px;
        -moz-border-top-colors: none;
        -moz-border-right-colors: none;
        -moz-border-bottom-colors: none;
        -moz-border-left-colors: none;
    }

    #contents div.vuln-detail h2 {
        color: #dddddd;
        background-color: #777777;
    }

    #contents div.legend {
        background-color: #282828;
        color: #dddddd;
    }

    #contents div.legend h3 {
        background-color: #383838;
        color: #dddddd;
    }



    #footer {
        color: #dddddd;
        background-color: #383838;
    }

    #footer a {
        background-color: #383838;
        color: #dddddd;
    }

    #highlights {
        background-color: #282828;
        color: #dddddd;
    }

    #highlights dd {
        background-color: #282828;
        color: #dddddd;
    }

    #highlights dt {
        background: #383838;
        color: #dddddd;
    }

    #highlights a {
        background-color: #282828;
        color: #dddddd;
    }

    #navbar a:hover {
        background: #888888;
        color: #dddddd;
    }

    #navmenu1 a {
        color: #dddddd;
        background-color: #666666;
        border-right: 1px solid #777777;
        border-bottom: 1px solid #777777;
    }

    #navmenu2 a {
        color: #dddddd;
        background-color: #484848;
        border-right: 1px solid #777777;
        border-bottom: 1px solid #777777;
    }
    table.data th {
        background-color: #383838;
    }

}
