css:
@-moz-document domain("www.pandora.com") {
  /* Colors */
  
  #playerBar {
      background-color: rgba(0, 0, 0, 0);
  }
  .contentnav {
      background-color: rgba(0, 0, 0, 0);
  }
  .stationContent {
      background-color: rgba(0, 0, 0, 0.3) !important;
      border: none !important;
  }
  .stationListItem .options {
      background: rgba(0, 0, 0, 0.5) !important;
  }
  .stationName.stationName_hover,
  .stationListItem.selected,
  .btn_bg_sort.selected {
      background-color: rgba(255, 255, 255, 0.2) !important;
  }
  .slidesBackground {
      background-color: none;
  }
  #body {
      background-color: #000 !important;
  }
  #currentSlideArrow,
  .friends,
  .myprofile,
  .stationListHeader {
      display: none !important;
  }
  /* Splash */
  
  #splash {
      background-color: #555;
      background-image: url('https://cdn.suwalls.com/wallpapers/abstract/dark-blue-glow-29218-1920x1080.jpg');
      background-repeat: no repeat;
      background-size: cover;
  }
  /* Top Bar */
  
  #brandingBar .middlecolumn {
      display: none !Important;
  }
  #brandingBar {
      background-image: none !important;
      background-color: #3a4a6a !important;
      color: #FFF !important;
      position: absolute !important;
      right: 0px !important;
      height: 25px !important;
      top: 70px !important;
      z-index: 2 !important;
      min-width: 0px !important;
      max-width: 0px !Important;
  }
  #topnav {
      top: -2px !important;
      max-height: 10px !important;
      min-height: 10px !important;
  }
  /* PlayBar */
  
  .top {
      display: none !important;
  }
  #playerBar {
      border-bottom: none !important;
      width: 100% !important;
      height: 68px !important;
      z-index: 1 !important;
      position: fixed !important;
      bottom: 18% !important;
      left: 47.5% !important;
  }
  .contentnav {
      border: none !important;
  }
  #playerBar .middlecolumn {
      width: 420px !important;
      padding-top: 15px;
      z-index: 999 !important;
      margin-left: -75px !important;
  }
  .genres {
      width: 100% !important;
      position: absolute !important;
      display: block !important;
      z-index: 5000 !important;
  }
  .item.narrative.clearfix {
      margin-left: 230px !important;
  }
  .searchBox {
      width: 175px !important;
      height: 29px !important;
      background-color: rgba(255, 255, 255, 0.2) !important;
      border-radius: 5px !important;
  }
  #playerBar .leftcolumn {
      position: fixed !important;
      left: 0% !important;
      top: 0% !important;
      width: 208px !important;
      margin: 3px 0 0 7px !important;
      z-index: 1 !important;
  }
  #playerBar .searchInput {
      top: 6px !important;
      left: 2px !important;
      font-size: 10pt !important;
      color: #cbcfd7 !important;
  }
  #playerBar .searchSubText {
      width: 150px !important;
      top: 15px !important;
      left: 37px !important;
      padding: 3px !important;
      font-size: 10pt !important;
      color: #cbcfd7 !important;
      overflow: hidden !important;
      text-overflow: ellipsis !important;
  }
  .rightcolumn {
      width: 1px !important;
      z-index: 1 !important;
  }
  #playerBar .rightcolumn .info {
      margin: 7px 9px 0 0;
      width: 1px !important;
      height: 62px !important;
      z-index: 1 !important;
  }
  .nowplaying,
  .nowplaying a:link {
      position: fixed !important;
      bottom: 68% !important;
      right: 51% !important;
      color: rgba(0, 0, 0, 0) !important;
  }
  .playerBarArt {
      width: 330px !important;
      height: auto !important;
      margin: 0 !important;
      padding: 0 !important;
      border-radius: 5px !important;
      box-shadow: 1px 1px 30px rgba(0, 0, 0, 0.5) !important;
      z-index: 1 !important;
  }
  #playerBar .rightcolumn .info a {
      font-size: 12px !important;
  }
  #playerBar .rightcolumn .cd_icon {
      margin: 6px 5px 0 0 !important;
  }
  .user_activator {
      background: none !important;
      border: 0px !important;
      margin: -65px 0 0 0 !important;
      font-size: 12pt !important;
      max-width: 300px !important;
  }
  .userName {
      position: fixed !important;
      top: 13px !important;
      right: 70px !important;
      max-width: 300px !important;
  }
  .userName_arrow {
      position: fixed !important;
      top: 35px !important;
      right: 70px !important;
      background-size: 10px 10px !important;
      background-image: url(http://www.iconsdb.com/icons/download/white/arrow-217-64.png) !important;
      min-width: 10px !important;
      min-height: 10px !important;
      opacity: 0.5 !important;
  }
  .userNameImage {
      height: 50px !important;
  }
  .userNameImage > img {
      border-radius: 5px !important;
      width: 50px !important;
      height: auto !important;
      position: fixed !important;
      top: 10px !important;
      right: 10px !important;
  }
  #brandingBar .rightcolumn {
      margin-top: -10px;
  }
  /* Stretching */
  
  .contentnav {
      position: fixed !important;
      top: 0px !important;
      left: 0px !important;
      width: 100% !Important;
      z-index: 1 !important;
  }
  .adSupported-layout #adLayout,
  .adSupported-layout .contentContainer,
  div#mainContentContainer.contentContainer div.content div#mainContent div.home div.homeRightSide div.slidesBackground div img {
      width: 100% !important;
  }
  .contentContainer .top,
  .view-2,
  .home .homeRightSide,
  .stationContent,
  #mainContent,
  .home.fxCol-cont-1.view-2,
  #mainContentContainer {
      border-radius: 0px !important;
      background: none;
  }
  #mainContainer {
      background-image: url("https://cdn.suwalls.com/wallpapers/abstract/dark-blue-glow-29218-1920x1080.jpg") !important;
      background-repeat: no-repeat;
      background-size: cover;
      margin-top: -95px;
  }
  .homeRightSide,
  .slideDragLimit {
      height: 28.5% !important;
  }
  .fxCol-cont-1 {
      background: none;
  }
  .col-prop-1 {
      max-height: 500px;
  }
  /* Main Container Positioning*/
  
  .stationContent {
      z-index: 0;
  }
  .contentnav {
      top: 0px !important;
  }
  #main {
      top: 310px !important;
  }
  /* Sidebar */
  
  #detailContainer {
      margin-left: 2px !important;
  }
  .stationContent {
      border: none !important;
      border-radius: 0px;
      z-index: 999 !important;
      width: 230px !important;
  }
  #stationList,
  .jspPane,
  .jspContainer {
      width: 230px !important;
      overflow: scroll !important;
  }
  .stationName {
      color: #ddd !important;
      text-shadow: none !important;
      font-size: 10pt !important;
      width: 230px !important;
  }
  .stationNameText {
      overflow: visible !important;
      width: 200px !important;
  }
  #shuffleIcon {
      margin-top: 8px;
  }
  .stationListItem .stationName .stationNameText.promotedStation {
      display: none !important;
  }
  .stationListItem.selected {
      border: none !important;
      background: rgba(255, 255, 255, 0.2) !important;
  }
  .stationListItem.selected .stationName {
      padding: 3px 0px 5px;
      margin-top: 5px;
      width: 230px !important;
  }
  .stationListItem .stationName {
      padding-top: 3px;
      width: 230px !important;
  }
  .stationListItem {
      margin-top: 5px;
      width: 230px !important;
  }
  .stationListItem .options {
      border: none;
  }
  .option.addVariety,
  .option.optionsItem {
      border: none !important;
      color: #ddd !important;
  }
  .stationListHolder {
      margin-top: 47px;
  }
  #stationSortButtons.clearfix {
      position: absolute;
      background: rgba(0, 0, 0, 1) !important;
      bottom: 0px;
  }
  #stationSortDate,
  #stationSortAlpha {
      border: none;
      color: #ddd !important;
      text-shadow: none !important;
      background: rgba(255, 255, 255, 0.1) !important;
      width: 115px;
  }
  #stationSortAlpha.btn_bg_sort.unselectable.selected,
  #stationSortDate.btn_bg_sort.unselectable.selected {
      background-color: rgba(255, 255, 255, 0.2) !important;
  }
  .stationContent {
      height: 100%;
      width: 230px !important;
      margin-top: 95px;
      position: fixed;
  }
  .jspContainer,
  .stationListHolder {
      height: 95% !important;
  }
  /* Slider > Background */
  
  .slide {
      display: none !important;
  }
  img.art {
      border-radius: 0px !important;
  }
  /* Slides > Background */
  
  .slidesBackground > div > img,
  .slidesBackground > img {
      display: none !important;
  }
  .slidesBackground {
      width: 100% !important;
  }
  .bottom {
      width: 100% !important;
  }
  .trackData {
      text-shadow: none !important;
      color: #ccc;
      text-align: center !important;
      margin: 0 auto !important;
      width: 34% !important;
      position: fixed !important;
      bottom: 29% !important;
      font-size: 12pt !important;
      z-index: 99 !important;
  }
  #trackInfo a:link {
      color: #fff !important;
      font-size: 12pt !important;
  }
  #trackInfoContainer {
      padding-left: 30% !important;
  }
  .slideDrag {
      display: none !important;
  }
  /* Slides > Thumbs */
  
  .thumbContainer {
      background: rgba(0, 0, 0, 0.6) !important;
      display: none !important;
      margin-top: -200px !important;
      height: 60px !important;
      width: 100% !important;
      position: fixed !important;
      top: 2% !important;
  }
  .thumbDown {
      margin-left: 52px !important;
  }
  .thumbUp,
  .thumbDown {
      background: none !important;
  }
  .slide .thumbContainer .thumbUp a:link,
  .slide .thumbContainer .thumbUp a:visited {
      background: url("http://pandora.com/img/player-controls/btn_up.png")no-repeat scroll 0% 0% transparent;
  }
  .slide .thumbContainer .thumbUp.indicator a:link,
  .slide .thumbContainer .thumbUp.indicator a:visited {
      background: url("http://pandora.com/img/player-controls/btn_up_indicator.png")no-repeat scroll 0% 0% transparent;
  }
  .slide .thumbContainer .thumbDown a:link,
  .slide .thumbContainer .thumbDown a:visited {
      background: url("http://pandora.com/img/player-controls/btn_down.png")no-repeat scroll 0% 0% transparent;
  }
  .slide .thumbContainer .thumbDown.indicator a:link,
  .slide .thumbContainer .thumbDown.indicator a:visited {
      background: url("http://pandora.com/img/player-controls/btn_down_indicator.png")no-repeat scroll 0% 0% transparent;
  }
  .slides_left_scroll.unselectable,
  .slides_right_scroll.unselectable {
      background: none;
      display: none !important;
      background-color: #360F52;
      height: 50px;
  }
  .slides_left_scroll.unselectable {
      border-radius: 0 5px 5px 0;
      background-image: url('https://cdn4.iconfinder.com/data/icons/arrows-9/100/arrow-9-512.png');
      background-size: 23px 40px;
      background-repeat: no-repeat;
  }
  .slides_right_scroll.unselectable {
      border-radius: 5px 0 0 5px;
  }
  /* Lyrics, Bio, etc */
  
  .lyricsText.unselectable {
      font-size: 16px !important;
      line-height: 22px !important;
  }
  #trackInfo {
      border: none !important;
      background: none !important;
  }
  #trackInfo a:link {
      color: #fff !important;
  }
  img.pauseWatermark {
      position: fixed !important;
      right: 34.3% !important;
      top: 25% !important;
      z-index: 99 !important;
  }
  #trackDetail {
      display: none !important;
      border: none !important;
      background-color: #fff !important;
  }
  #trackInfoButtons {
      display: none !important;
      background-color: transparent;
      border: none;
      /* width: 330px;
    margin: -55px auto 20px auto; */
  }
  #trackDetail > div.item.relatedArtists {
      display: none !important;
      margin-bottom: 12px !important;
  }
  .searchPopupContainer {
      position: fixed;
      top: 55px;
      left: 14px;
      width: 296px;
  }
  /* Ads, Logo, Footer, etc. */
  
  #ad_container,
  #accountMessageRibbon,
  .track_detail_close,
  .divider.last,
  #videoPageInfo,
  #footer,
  .stationChangeSelectorNoMenu,
  .logo,
  .upgradeLink,
  .user_menu + SPAN,
  #upgradeOffer {
      display: none !important;
      height: 0px !important;
  }
  #promobox,
  .platformPromo.view-3.clearfix {
      display: none !important;
  }
}
