css:
@-moz-document domain("solarmovie.sc"), domain("solarmoviez.to") {
body {
    background-color: #222!important;
    color: #fff!important;
}

header {
    background-color: rgba(0,0,0,0.9)!important;
    position: fixed!important;
}

#menu, .sub-container {
    background-color: rgba(0,0,0,0.9)!important;
}

.page-cover {
    height: 100%!important;
}

.page-cover:after {
    background-color: rgba(0,0,0,0.7)!important;
    color: #fff!important;
}

.page-cover:before {
	opacity: 0.5!important;
}

#mv-keywords {
    width: 95vw!important;
}

.search-suggest a, ul.dropdown-menu a {
    color: #555!important;
}

li a {
    color:#aaa!important;
}

#bar-player {
    background-color: #141414!important;
}

.pa-server {
    background-color: #141414!important;
}

a, span, ::after, li.active a, li:before {
    color: #fff!important;
}

.block-trailer {
    background: rgba(255,255,255,0.95)!important;
}

.block-trailer:hover {
    background: rgba(255,255,255,0.6)!important;
    cursor: pointer!important
}

.movies-list-wrap {
    margin-top: 30px!important;
}

#like-home, .social-share, .pw-comment, .block-social, footer, .zopim {
    display: none!important;
}

.play-eps::before, .play-eps::after, .play-eps {
    background: none!important;
}

.logged-user img {
    content: url(https://www.brightfind.com/images/services/design/icon-design-users.png);
}

@media (max-width: 1000px) {
.page-cover {
	height: 350px!important;
}
}
}
