.list-row {
    background-color: rgba(255,255,255,0.7)!important;
    border-bottom: 1px solid rgba(255,255,255,0.4)!important;
    color: #333!important;
    border-radius: 0px!important;
}


/* Misc */

.nav-menu--open {
    background-color: rgba(255,255,255,0.97)!important;
    margin: 10px!important;
    width: 95%!important;
    height: 92%!important;
    border-radius: 10px!important;
}

.rapid-task-creator__icon:before {
    content: "+"!important;
    color: #fff!important;
    margin-top:-5px!important;
    font-size: 60px!important;
    line-height: 25px!important;
    text-align: center!important;
}                                  

.handle__toggle-container {
    border-color: #222!important;
}
.handle__iframe-container--side-bar {
    background-color: rgba(255, 255, 255, 0);
}
.handle__toggle-button--active {
    background-color: #222!important;
}
.handle__toggle-button--inactive:hover {
    background-color: #222!important;
}
.fc-today {
    background-color: #eee!important;
}
.list-header {
    background-color: transparent;
    line-height: 45px;
    border-bottom: 1px solid rgba(255,255,255,0.3);
}
.list-header__button {
    color: #737373!important;
}
.list-header__button:hover {
    color: #47bcc9!important;
}
.list-row-task-icon {
    color: #cccccc!important;
}
.list-row-task-link .due-date, .list-row-task-link .list-row-task-project-name, .list-row-task-link .reminder-date {
    color: #555!important;
}
.help-box {
    display: none!important;
}
.handle__toggle-button .handle__toggle-button__icon {
    background-image: url(http://i.imgur.com/OnFzPia.png)!important;
}
.rapid-task-creator {
    box-shadow: none!important;
}
.rapid-task-creator.collapsed {
    overflow: hidden!important;
}
.nav-menu__group.nav-menu__group--calendars {
    display: none!important;
}
.one-click-actions {
    padding: 0px 20px 0px 0px;
    background-color: rgba(255,255,255,0)!important;
}
}
