css:
@-moz-document domain("www.vlive.tv"), domain("channels.vlive.tv") {
#content {
    background-color: rgb(35, 34, 35);
}
#container {
    background-color: #00c4ff;

}

#body {
    background-color: rgb(35, 34, 35);
}









#vlive_info {
    background-color: rgb(35, 34, 35);
}
.vlive_area {
    background-color: rgb(66, 66, 66);
}
#header {
    position: relative;
    z-index: 3000;
    background-color: #424242;
}

#footer {
    position: absolute;
    bottom: 0;
    width: 100%;
    height: 150px;
    background: #2d2d2d;
    border-top: 1px solid #00c9ff;
    text-align: center;
    font-size: 0;
}
#container {
    background-color: #232223;
}

.info_area .name {
    color: #6495ed;
}
.vlive_top .star_profile .info_area .name {
    color: #b11e1e
}
.btn_follow {
    color: #bfbfbf;
}

.btn_follow, .channel_lst_ct .btn_buy {
    position: relative;
    z-index: 2;
    height: 33px;
    line-height: 13px;
    border: 1px solid #5a0909;
    background: transparent;
    border-radius: 31px;
    padding: 0 17px 0 14px;
    font-size: 14px;
	color: #7d7d7d;
 
}

.vlive_info .txt {
    color: #ffffff;
}

.vlive_cont {
    zoom: 1;
    height: 581px;
    border-bottom: 1px solid #000;
    background: #000;
}
.vlive_info {
    position: relative;
    padding-left: 19px;
    height: 82px;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

.video_list .video_tit {
    color: #fff;

}
.video_area h3.tit,
.banner_area h3.tit {
    color: #b11e1f;
}
.events_list .text_area .title a {
    color: #ad8989;
    
}
.events_list .text_area .desc {
    color: #cecece;
}
.home_profile_area .bg_color,
.channel_lst .bg_color {

    background-color: #353535;
}
.home_profile_area .info_area .name,
.channel_lst .info_area .name {
    color: #ffffff;
}
.home_profile_area .info_area .txt,
.channel_lst .info_area .txt {
    color: #bbbbbb;
}


.u_cbox .u_cbox_head .u_cbox_title {
    color: #ffffff;}

.u_cbox {
    position: relative;
    background-color: #292828;
    text-align: left;
    -webkit-text-size-adjust: none;
}

.u_cbox .u_cbox_refresh_wrap {
    border-bottom: 1px solid #000;
    border-right: 1px solid #000;
    background-color: #292828;
}

.u_cbox .u_cbox_content_wrap {
    border-right: 1px solid #000;
}

.u_cbox .u_cbox_head {
    border-bottom: 1px solid #000000;
    border-right: 1px solid #000;
}


.u_cbox .u_cbox_list .u_cbox_comment_box {
    border: 1px solid #000000;
    background: #212020;
}

.comment_area {
    border-top: 1px solid #000;
}


.u_cbox .u_cbox_write_wrap .u_cbox_write .u_cbox_write_inner {
    border: 1px solid #000;
}

.u_cbox .u_cbox_write .u_cbox_write_inner {
    border: 1px solid #b3b3b3;
}

.u_cbox .u_cbox_write .u_cbox_inbox {
    background-color: #292828;
}

.u_cbox .u_cbox_list .u_cbox_text_wrap {
    padding-left: 38px;
    font-size: 12px;
    color: #b3b3b3;
    max-width: 213px;
    line-height: 16px;
}
.u_cbox .u_cbox_list .u_cbox_in_report {
    color: #6d4a4a;
    font-size: 11px;
}
.u_cbox .u_cbox_list .u_cbox_date {
    color: #565656;
    font-size: 11px;
}
.u_cbox .u_cbox_write .u_cbox_write_inner {
    position: relative;
    background-color: #292828;
    border: 0px solid #b3b3b3;
}

.u_cbox .u_cbox_content_layer_link {
    display: inline-block;
    padding: 0 12px 0 11px;
    background-color: rgba(97, 97, 97, 0.9)!important;
    border: 1px solid rgba(0,0,0,.1);
    border-bottom: 1px solid rgba(0,0,0,.2);
    border-radius: 16px;
    line-height: 30px;
    text-align: center;
    -webkit-box-shadow: 0 1px 2px rgba(4,0,0,.2);
    -moz-box-shadow: 0 1px 2px rgba(4,0,0,.2);
    box-shadow: 0 1px 2px rgba(4,0,0,.2);
}

.u_cbox .u_cbox_btn_refresh .u_cbox_ico_refresh {
    display: block;
    width: 23px;
    height: 23px;
    border: 1px solid #000000;
    background-position: -77px -55px;
    background-color: #000;
}





.u_cbox .u_cbox_refresh_wrap .u_cbox_bg_wrap {
    display: block;
    position: absolute;
    left: 10px;
    right: 9px;
    height: 19px;
    border-top: 1px solid #3b3b3b;
    border-bottom: 1px solid #3b3b3b;
    background-color: #292828;
}


.gnb_info .lyr {
  
    position: absolute;
    top: 55px;
    right: 0;
    width: 111px;
    border: 1px solid #dedede;
    background-clip: border-box;
    border: 1px solid rgba(241, 241, 241, 0.23);
    border-radius: 3px;
    background-color: rgb(23, 30, 37);
    font-size: 13px;
    text-align: center;
}

.gnb_info .lyr a {
    display: block;
    line-height: 36px;
    color: #6796a2;
}

.upcoming_list li .text_area .title a {
    display: -webkit-box;
    overflow: hidden;
    text-overflow: ellipsis;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    margin-top: 1px;
    font-size: 19px;
    line-height: 23px;
    font-weight: normal;
    text-decoration: none;
    color: #1ecfff;
}


.upcoming_list li {
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    position: relative;
    overflow: hidden;
    padding: 15px 0 15px;
    border-top: 1px solid #fff;
    border-bottom: 1px solid #fff;
    color: #ad9494;
}

.upcoming_list li .time {
    float: left;
    width: 93px;
    padding-left: 19px;
    margin-right: 31px;
    margin-top: 2px;
    font-size: 17px;
    color: #b90808;
}

.date_list li .date {
    padding-right: 7px;
    font-size: 19px;
    font-weight: bold;
    color: #086dab;
    vertical-align: middle;
}

.upcoming_list li.on {
    background-color: #004a5f;
    border-top: 1px solid #1ecfff;
}




.vlive_channel_menu {
    display: none;
    background-color: #424242;
}

.vlive_channel_menu, .store_channel_menu {
    height: 130px;
    border-bottom: 1px solid #00c7ff;
        background-color: #424242;
} 
    



/*------------TV-------------*/

.cont_spot {
    background-color: #2d2d2d;
    padding: 28px 0 27px;
    border-top: 1px solid #bbf0ff;
    border-bottom: 1px solid #1ecfff;
}



.video_list .video_txt_inner .video_tit {
    max-height: 48px;
    font-size: 16px;
    line-height: 24px;
    color: #9e8e8e;
}

.video_list .video_txt_area {
    position: relative;
    background-color: #424242;
}

.tag_aside_tit {
    font-size: 16px;
    color: #5b6666;
}

.tag_aside_txt {
    font-size: 14px;
    color: #696969;
}
.ranking_txt {
    color: #0494bb;
}


.top_channel_menu .top_channel_tit {
    color: #949494;
}
/*------------TV-------------*/





/*- edit --*/


.home_profile_area h3.tit, .channel_lst h3.tit, .tag_area h3.tit {
    color: #923b3b;
}


.title_upcoming {
    overflow: hidden;
    display: table;
    width: 100%;
    height: 170px;
    background: #484839;
    border-bottom: 1px solid #e1df11;
    text-align: center;
}



.title_celebs {
    overflow: hidden;
    display: table;
    width: 100%;
    height: 170px;
    border-bottom: 1px solid #f4f107;
    background-color: #484839;
    text-align: center;
}

.title_events {
    overflow: hidden;
    display: table;
    width: 100%;
    height: 169px;
    background-color: #484839;
    border-bottom: 1px solid #f4f107;
}








.store_sticker_list .btn_coin .txt {
    margin-left: 3px;
    font-size: 14px;
    line-height: 18px;
    color: #dadada;
}

.store_sticker_list .sticker_link .tit {
    display: -webkit-box;
    overflow: hidden;
    text-overflow: ellipsis;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    max-height: 34px;
    font-size: 13px;
    line-height: 18px;
    color: #7d5959;
    word-break: break-all;
}






.tag_select .select_list {
    background-color: #1b1b1b;
    margin-bottom: -1px;
}

.tag_select .select_list li {
    width: 91px;
    border-bottom: 1px solid #424242;
}






/*---chanel---*/

.videoArea[_ngcontent-c2] {
    clear: left;
    position: relative;
    margin-top: 30px;
    margin-bottom: 40px;
    font-size: 14px;
    background: #232223;
}





.videoTit[_ngcontent-c3] {
 
    color: #a2a2a2;
}

.channelHome .homeTab {
    overflow: hidden;
    width: 100%;
    height: 49px;
    border-bottom: 1px solid #00c7ff;
    background: #424242;
    text-align: center;
}

.channelHome .homeTab .homeTabLst li a {
    display: block;
    height: 49px;
    position: relative;
    color: #888888;
    font-size: 18px;
    line-height: 50px;
}


.noticeArea .noticeList .list>li .inner .info .box .title span {
    display: inline-block;
    overflow: hidden;
    max-width: 486px;
    white-space: nowrap;
    word-break: normal;
    word-wrap: normal;
    text-overflow: ellipsis;
    font-size: 24px;
    color: #d4d4d4;
    vertical-align: top;
}






.boardView {
    background-color: #232223;
}





