css:
@-moz-document url("http://petridish.pw/ru/"), url("http://petridish.pw/en/") {
/*Тёмный фон*/

#top-time,
.top-serv,
.serv-stat-content,
.top,
.wrapper,
.option,
.option-content-header,
.top-serv-header,
.auth-content,
.popup,
.share-result,
.server-filtr:hover,
.server-filtr:focus,
.server-filtr.active {
    background: #282828 !important;
}
/*Текст*/

.mode-text,
strong,
p,
h1,
h2,
h3,
.popup span,
.option span,
.liked-list span,
.liked-list div,
.liked-list i,
.ui-tab:not(.ui-tabs-active) a,
td.number,
.mdi-close,
.server-list div,
.server-list i,
i.mdi-help-circle,
.block .player-name,
.block .player-name a,
#endgameoverlay span,
#endgameoverlay li,
.savedremove,
.mdi-arrow-left {
    color: rgba(255, 255, 255, 0.85) !important;
}
/*Черный*/

div.menu i.mdi-account,
.auth span {
    color: #000 !important;
}
/*Синий */

.rate,
.skin-content a {
    color: rgba(38, 111, 255, 0.6) !important;
}
/*Не important */

a {
    color: rgba(255, 255, 255, 0.85)
}
/*canvas после смерти(белый)*/

.canvasjs-chart-container {
    display: none;
	width: 25%;
}
/*Кнопки справа колонной на маленьком монике*/

/*[[qqq]]*/
/*[[www]]*/
/*Топ,ник юзера*/

li.me p {
    color: #ff268d!important;
}
}
