css:
@-moz-document url-prefix("https://pt.wikipedia.org/wiki/") {
div#mw-panel {
    font-size: inherit;
    position: absolute;
    top: 160px;
    padding-top: 1em;
    width: 10em;
    left: 0;
}
div#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
}
div#simpleSearch #searchInput {
    width: 100%;
    height: 3.7em;
    padding: 0.2em 0px 0.2em 1.2em;
    /* margin-left: 100px; */
    font-size: 13px;
    direction: ltr;
    -webkit-appearance: textfield;
}
a.new, #p-personal a.new {
    color: #b13b3b;
}
a.new:visited, #p-personal a.new:visited {
    color: #a56b6b;
}
a {
    text-decoration: none;
    color: #4173b9;
    background: none;
}
a:visited {
    color: #9fb9e2;
}
.catlinks {
    border: 10px solid #1e2838;
    background-color: #1e2838;
    /* padding: 5px; */
    margin-top: 1em;
    clear: both;
}
#toc, .toc, .mw-warning, .toccolours {
    border: 10px solid #1e2838;
    background-color: #1e2838;
    padding: 5px;
    font-size: 95%;
}
#p-search form, #p-search input {
    margin: 0px;
    margin-top: -20px;
    margin-right: 150px;
}
div#simpleSearch {
    display: block;
    width: 12.6em;
    width: 20vw;
    min-width: 5em;
    max-width: 20em;
    padding-right: 1.4em;
    height: 3em;
    margin-top: 0.65em;
    position: relative;
    min-height: 1px;
    border: solid 0px #aaa;
    color: #000;
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAQCAIAAABY/YLgAAAAJUlEQVQIHQXBsQEAAAjDoND/73UWdnerhmHVsDQZJrNWVg3Dqge6bgMe6bejNAAAAABJRU5ErkJggg==);
    background-position: top left;
    background-repeat: repeat-x;
}
div#simpleSearch input {
    margin: 0;
    padding: 0;
    border: 0;
    background-color: #1e2838;
    color: #fff;
}
div.vectorTabs {
    float: left;
    height: 2.5em;
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAIAAABmjeQ9AAAAQ0lEQ…OcUegfarWjP3ojZvEzxs6j+nygmo+zzsk79nY+tOxdEhlf3UHVgUFrVwAAAABJRU5ErkJggg==);
    background-position: bottom left;
    background-repeat: no-repeat;
    padding-left: 1px;
    visibility: hidden;
}
div.vectorMenu {
    direction: ltr;
    float: left;
    cursor: pointer;
    position: relative;
    visibility: hidden;
}
#p-personal {
    position: absolute;
    top: 0.33em;
    right: 0.75em;
    z-index: 100;
    visibility: hidden;
}
#left-navigation {
    float: left;
    margin-left: 10em;
    margin-top: 2.5em;
    margin-bottom: -2.5em;
    display: inline;
    visibility: hidden;
}
div.vectorTabs ul {
    float: left;
    height: 100%;
    list-style-type: none;
    list-style-image: none;
    margin: 0;
    padding: 0;
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAIAAABmjeQ9AAAAQ0lEQ…OcUegfarWjP3ojZvEzxs6j+nygmo+zzsk79nY+tOxdEhlf3UHVgUFrVwAAAABJRU5ErkJggg==);
    background-position: right bottom;
    background-repeat: no-repeat;
    visibility: hidden;
}
.mw-body {
    margin: 10em;
    padding: 1em;
    border: 0px solid #a7d7f9;
    border-right-width: 0;
    margin-top: -1px;
    background-color: rgb(36, 50, 70);
    color: #ababab;
    direction: ltr;
}
body {
    background-color: rgb(36, 50, 70);
}
h1, h2, h3, h4, h5, h6 {
    color: #f9f9f9;
    background: none;
    font-weight: normal;
    margin: 0;
    overflow: hidden;
    padding-top: .5em;
    padding-bottom: .17em;
    border-bottom: 1px solid #a2a9b1;
}
#mw-head-base {
    margin-top: -5em;
    margin-left: 10em;
    height: 5em;
    background-color: rgb(36, 50, 70);
}
#mw-page-base {
    visibility: hidden;
    background-position: bottom left;
    background-repeat: repeat-x;
    background-color: rgb(36, 50, 70) !important;
}
div#footer {
    margin-left: 0em;
    margin-top: 0;
    padding: 0em;
    visibility: hidden;
}
.infobox_v2 {
    background: rgb(30, 40, 56);
    color: #8a97ab;
    font-size: 90%;
    line-height: 1.1em;
    float: right;
    clear: right;
    margin: 0 0 .5em 1em;
    width: 23em;
    border: 10px solid rgb(30, 40, 56);
}
.infobox_v2 th {
    vertical-align: top;
    text-align: left;
    background-color: #1e2838 !important;
    color: white;
    padding: 10px;
}
.infobox_v2 .topo {
    height: 45px;
    vertical-align: middle;
    text-align: center;
    font-size: 120%;
    font-weight: bolder;
    line-height: 1.2em;
    color: #fff;
}
div.thumbinner {
    border-left: 10px solid #1e2838;
    border-right: 10px solid #1e2838;
    border-top: 10px solid #1e2838;
    border-bottom: 10px solid #1e2838;
    padding: 3px;
    background-color: #1e2838;
    font-size: 94%;
    text-align: left;
    overflow: hidden;
}
.mw-content-ltr .thumbcaption {
    text-align: left;
    padding-top: 10px;
}
.mw-body a.external:visited {
    color: #bd83bd;
}
.navbox td {
    background: #29313e !important;
    padding: 5px;
}
.navbox th {
    background: #20262f !important;
    padding: 5px;
}
table.toccolours th {
    background: #2a3c58;
}
table tr {
    background-color: #1e2838 !important;
}
table th {
	background-color: #37465f !important;
}
table.wikitable {
    margin: 1em 0;
    background-color: #f8f9fa;
    border: 1px solid #a2a9b1;
    border-collapse: collapse;
    color: #fff;
}
td, th {
    border: 10px !important;
    background-color: #1e2838 !important;
}
#siteNotice {
    font-size: 0.8em;
    background: rgb(255, 255, 255);
    width: 1000px;
}
table.wikitable {
    margin: 1em 0;
    background-color: #f8f9fa;
    border: 0px solid #1e2838;
    border-collapse: collapse;
    color: #fff;
}   
table.navbox {
    margin-top: 1em;
    background: #1e2838 !important;
    border: 1px #1e2838 !important;
}
div.NavFrame div.NavHead {
    height: 1.6em;
    font-weight: bold;
    font-size: 100%;
    background-color: #1e2838 !important;
    position: relative;
}
table.vertical-navbox, nowraplinks {
    background: #1e2838 !important;
    border: 10px solid #1e2838 !important;
}
#p-logo a {
    display: block;
    width: 0px;
    height: 0px;

}
#p-logo a::after {
    background-image: url('http://i.imgur.com/lbYsLqA.png');
    background-size: 85.3px 80px;
    display: inherit;
    width: 85.3px; 
    height: 80px;
    content:"";
    position: relative;
    left: 10em;
}
div#mw-panel div.portal {
    margin: 0 0.6em 0 0.7em;
    padding: 0.25em 0;
    direction: ltr;
    visibility: hidden;
}
}

