css:
@-moz-document regexp("^https?://stackoverflow.com.*") {
/*
 * Created by RO_engineer - under development
 */

#search {
    display: none;
}

body > header.top-bar {
    background-color: #445;
    border-style: solid;
    border-bottom: 3px solid #F48024;
    height: 55px;
}

header.top-bar .navigation .-item._current a.-link {
    color:#ff8;
    border-bottom: 3px solid orange;
}

header.top-bar .navigation .-item._current a.-link:hover {
    color: black;
}

header.top-bar .navigation a.-link {
    color:#fff;
    font-size: 16px;
    height: 49px;
    top: 0px;
}

header.top-bar .my-profile .-rep {
    color: white;
    font-size: 18px;
    padding-right: 10px;
}

#nav-docs > small {
    display: none;   
}

#nav-docs {
    display: none;   
}

body > header > div > div.-actions > a:hover div.-rep.js-header-rep {
    color: black;
}

body > header > div > div.-actions > a > div.-badges span.badgecount {
    font-size: 16px;
}

body > header > div > div.-actions > nav > ol > li:nth-child(3) {
    display: none;
}

.top-bar .my-profile .-badges .badge1, .top-bar .my-profile .-badges .badge2, .top-bar .my-profile .-badges .badge3 {
    width: 12px;
    height: 12px;
}


/* logged out user - logo */

body > header.top-bar.js-top-bar._fixed > div > div.-main > a.-logo {
    width: 170px;
    -webkit-filter: invert(0.7) brightness(1.9);
    filter: invert(0.7) brightness(1.9);
    height: 49px;
    top: 4px;
}

body > header.top-bar.js-top-bar._fixed > div > div.-main > a.-logo:hover {
    background-color: #eff0f1;
    width: 170px;
    -webkit-filter: none;
    filter: none;
}

/* logged in user - logo */

body > header > div > div.-main > a.-logo {
    width: 170px;
    -webkit-filter: invert(0.7) brightness(1.9);
    filter: invert(0.7) brightness(1.9);
    height: 49px;
    top: 4px;
}

body > header > div > div.-main > a.-logo:hover {
    background-color: #eff0f1;
    width: 170px;
    -webkit-filter: none;
    filter: none;
}

body > header > div > div.-actions > nav > ol > li > a.-link {
    height: 49px !important;
    top: 0px;
}

body > header > div > div.-actions > nav > ol > li > a.-link > svg {
    filter: brightness(0) invert(1);
    -webkit-filter: brightness(0) invert(1);
    vertical-align: initial !important;
}

body > header > div > div.-actions > nav > ol > li > a.-link:focus > svg {
    filter: brightness(0);
    -webkit-filter: brightness(0);
}

body > header > div > div.-actions > nav > ol > li > a.-link:hover > svg {
    filter: brightness(0);
    -webkit-filter: brightness(0);
}

body > header > div > div.-main > a.-logo > span.-img._glyph {
    width: 170px;
    background-position: 0 -500px;
}

#sidebar > div.module.community-bulletin {
    display: none;   
}

#footer > div {
    display: none;
}

body > header > div > div.-actions > nav.secondary-nav > ol > li {
    padding-right: 2px;
    padding-left: 2px;
}

body > header > div > div.-actions > a.my-profile.js-gps-track {
    height: 49px;
    top: 4px;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge1+span.badgecount {
    color: #db2;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge2+span.badgecount {
    color: #ccd;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge3+span.badgecount {
    color: #c97;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge1 {
    text-shadow: 5px 2px #000;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badgecount {
    text-shadow: 1px 1px #000;
}

body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge1, body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge2, body > header > div > div.-actions > a.my-profile.js-gps-track > div.-badges > span > span.badge3 {
    vertical-align: top;
    margin-right: 1px;
    margin-left: 4px;
    -moz-transform: scale(2,2);
    -webkit-transform: scale(2,2);
    transform: scale(2,2);
}

body > header > div > div.-actions > nav > ol > li.-item > a.js-site-switcher-button {
    display: none;   
}

.indicator-badge.js-unread-count._positive {
    font-size: 16px;
    background-color: #4b2;
    color: white;
}

body > header > div > div.-actions > a > div.gravatar-wrapper-24 > img {
    width: 50px;
    height: 50px;
    border: 1px solid;
    border-color: black;
}

body > header > div > div.-actions > a > div.gravatar-wrapper-24 {
    border-radius: 7px;
}

body > header > div > div.-actions > a.my-profile.js-gps-track {
    padding: 0px 10px;
}

/* logged out */
body > header > div > div.-actions > div > a.login-link.btn-clear {
    color: white;
}

}
