css:
@-moz-document domain("www.patheos.com") {
/*
 * Name:    Patheos: Dark
 * Author:  cr0sh
 * Version: v0.0.1
 */

/* Background */
html, 
body,
#wrap,
#inner
#header, 
.post, 
.entry-title,
.entry-title > a,
.menu-item > a,
.entry-content > p,
.post-meta {
	background-color: #101010 !important;
    color: #c5c5c5 !important
}

.entry-content > blockquote {
    background-color: #c5c5c5 !important;
	color: #101010 !important;
}

#patheos-header {
    background: none;
	background-color: #101010 !important;
    color: #c5c5c5 !important
}

.menu-item > a:hover
{
    background-color: #c5c5c5 !important;
	color: #101010 !important;
}

#sidebar,
.widget-title {
    background-color: #202020 !important;
    color: #c5c5c5 !important
}

.patheos-newsletter-mod-rail p,
#recentcomments .dsq-widget-comment p {
    color: #c5c5c5 !important
}

.author-box p {
  	background-color: #101010 !important;
    color: #c5c5c5 !important
}

#subnav ul {
	background-color: #101010 !important;
}

#subnav li a {
    border-color: #101010 !important;
}

.navigation li a {
  	background-color: #101010 !important;
    color: #c5c5c5 !important
}

.navigation li a:hover
{
    background-color: #c5c5c5 !important;
	color: #101010 !important;
}

.navigation li.active a {
    background-color: #c5c5c5 !important;
	color: #101010 !important;
}

#footer-wrp {
    background: none;
	background-color: #101010 !important;
    color: #c5c5c5 !important
}
}
