css:
@-moz-document domain("docs.angularjs.org") {
body {
    background-color: #130d0d;
    color: #ddd;
}

.variables-matrix tr:nth-child(even) td, .variables-matrix th {
    background-color: #1f1818;
}

.variables-matrix, .variables-matrix td, .variables-matrix td + td, .variables-matrix th + th {
	border-color: #291f1f;
}

.pln {
    color: #ddd;
}

.com {
    color: #404040;
}

.kwd {
    color: #55ff55;
}

.pun {
    color: #595d65
}

.tag {
    color: #428bca;
}

pre, code, p > code {
    background-color: #03040a;
}

.sup-header, #navbar-sub, #navbar-notice .navbar-inner {
    background-color: rgba(3, 4, 10, 0.88)
}

.picker {
    border: 1px solid #000000;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #000000), color-stop(100%, #191919));
    background-image: -webkit-linear-gradient(#000000, #191919);
    background-image: -moz-linear-gradient(#000000, #191919);
    background-image: -o-linear-gradient(#000000, #191919);
    background-image: linear-gradient(#000000, #191919);
}
}

@-moz-document domain("angularjs.org") {

}
