css:
@-moz-document url-prefix("http://m.mangafox.me/") {
.flexslider,
.adv-here,
footer,
.mobile-banner, .bg-caw {
    display: none !important;
}
.genres-manga {
    padding:0;
}
.top-search {
    position: initial;
    background: #75b520;
    padding-top: 5px;
    padding-bottom: 7px;
}
.search-top form input {
	color:white !important;
}
.top-search-content {
    background: white;
    margin-left: 10px;
    margin-right: 10px;
}
.post,
.cover {
    width: 118px !important;
    height: 184px !important;
    border: 0 !important;
}
.cover img {
    border-top-right-radius: 4px;
    border-top-left-radius: 4px;
}
.cover p {
    text-align: center !important;
    background-image: linear-gradient(to bottom, rgba(117, 181, 32, 0), #231815) !important;
}
.manga-list {
    padding: 0;
    letter-spacing: -4px;
}
.manga-list li {
    margin: 1px;
    height: 207px !important;
}
.post p {
    color: #ffffff;
    text-align: center;
}
.manga-list li .post p.title {
    background: #231815;
    padding-top: 1px;
    padding-bottom: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}
.mangaread-main {
    padding-top: 0px !important;
}
.roll-page {
    visibility: hidden !important;
    margin: 0;
    padding: 0;
    line-height: 0;
    border: 0;
}
#viewer {
    margin: 0;
    padding: 0;
}
input.search-icon {
    background-color: #58920b !important;
    height: 31px !important;
    color: #ffffff !important;
}
.menu ul li a {
    border: 0;
}
.menu ul li {
    width: 33.3%;
    max-width: 200px;
}
.menu {
    height: initial;
}
body {
    overflow-x: visible;
}
#infscr-loading {
    color: white !important;
    background: rgba(0, 0, 0, 0.72);
    font-weight: normal;
    text-decoration:none;
}
#infscr-loading img {
    display: none;
}
.cover-info {
    padding-left: 130px !important;
    color:black !important;
}
.cover-info p, .manga-detail-top .title, .detail-info p {
    color:black !important;
}
.detail-info p:first-child a {
    color: #0077cc !important;
}
.manga-chapters a, .manga-chapters, .chtitle span, .manga-summary {
    color:black !important;
}
.newch {
    color: #75b520 !important;
    font-weight:bold;
}
iframe {
    display:none !important;
}

.detail-menu li a {
    color: #75b520;
 	border-right:0px;
}
.detail-menu {
    border: 1px solid #75b520;
    border-right:0;
    border-left:0;
}
.detail-menu li {
    width: 33.31%;
}
.manga-genres ul li a {
    background:#75b520;
    border: 1px solid #75b520;
	padding: 0 10px 0px;
    color:white;
    border-radius:3px;
    margin:0;
    margin-bottom: 5px;
}
.manga-genres ul li a:active {
    color:white;
	background-color: #231815;
    border-color: #322420;
}
.manga-genres ul li a i {
    display:none;
}
.genres-list li a, .status-list li a {
    color:black;
}
.genres-list li a:active {
    background:#75b520;
    border-color:#75b520;
    color:white;
}
}

@-moz-document url-prefix("http://m.mangafox.me/search") {
.cover img {
    border-radius: 6px;
}
.post-list {
    border-radius: 3px;
}
}
