@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);

/*   Outlook 2010 Mobile Web App - Firefox Stylish Add-On Theme
     Copyright David Blanchard - 2017
     https://github.com/dblanchardDev/OWA_Mobile 
     
     License: CC BY-SA - Creative Commons 4.0 Attribution-ShareAlike
     https://creativecommons.org/licenses/by-sa/4.0/legalcode 
*/

/*===============================================================================================*/
/*= GLOBAL STYLES ===============================================================================*/
@-moz-document regexp("^http(?:s|):\/\/(?:messenger|outlook)\..*\..*\/.*$") {
  body {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 100%;
  }

  table.mnTbl {
    display: block;
  }
  
  /***********************************************************************************************/
  /* MATERIAL ICON SETUP *************************************************************************/
  @font-face {
    font-family: 'Material Icons';
    font-style: normal;
    font-weight: 400;
    src: local('Material Icons'),
         local('MaterialIcons-Regular'),
         url(https://fonts.gstatic.com/s/materialicons/v21/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format('woff2');
  }
  
  .mnTbl > tbody > tr:nth-child(1) > td:nth-child(1)::before,
  table.ob td::before,
  #schBtn::before,
  #clrBtn::before,
  table.pnt td > a::before,
  table.snt .fld a::before,
  #lnkGotoFldr::before,
  #lnkBrwsAllFldrs::after,
  table.snfmb a::before,
  table.sncts .fld a::before,
  table.dpht td a::before,
  table.dphst td a::before,
  table.tbhd td a::before,
  table.stb a.btn::before {
    font-family: 'Material Icons';
    font-weight: normal;
    font-style: normal;
    font-size: 50px;
    display: inline-block;
    line-height: 1;
    text-transform: none;
    letter-spacing: normal;
    word-wrap: normal;
    white-space: nowrap;
    direction: ltr;
    color: rgba(0, 0, 0, 0.8);

    text-rendering: optimizeLegibility;
    -moz-osx-font-smoothing: grayscale;
  }
  
  /***********************************************************************************************/
  /* TOP-TABLE - HEADER **************************************************************************/
  .mnTbl > tbody:nth-child(1) > tr:nth-child(1) {
    display: flex;
    height: 105px;
    overflow: hidden;
    border-bottom: 10px solid #0972C5;
  }

  /* Logo and Menu Button **********************/
  img#imgLogo {
    height: 100px;
    width: auto;
    padding-top: 10px;
  }

  .mnTbl > tbody > tr:nth-child(1) > td:nth-child(1)::before {
    content: "menu";
    vertical-align: top;
    padding: 14px 8px 0 20px;
    font-size: 75px;
  }

  /* Search Bar ********************************/
  td.w100:nth-child(1) {
    position: absolute;
    width: 100%;
    top:110px;
    left: 0;
    border-bottom: 5px solid #0972C5;
    opacity: 0;
    transition: opacity 300ms ease;
    pointer-events: none;
    z-index: 10;
  }

  td.w100:nth-child(1):focus-within {
    opacity: 1;
    pointer-events: all;
  }

  td.w100:nth-child(1) input,
  td.w100:nth-child(1) select {
    font-size: xx-large;
  }

  #tblSch {
    width: 100%;
    overflow: hidden;
  }

  #tblSch tr {
    background-color: #0972C5;
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-end;
    padding: 3px 0;
  }

  #tblSch tr td {
    display: inline-block;
    margin-right: 5px;
  }

  #tblSch tr td:nth-child(1) {
    display: block;
    flex-basis: 100%;
  }

  #tblSch input {
    position: absolute;
    width: 100%;
    height: 44px;
    padding: 0 15px 0 360px;
    top: 2px;
    left: 0;
    pointer-events: all;
  }

  #tblSch select,
  #tblSch a {
    margin-top: 49px;
  }

  #schBtn,
  #clrBtn {
    display: block;
    height: 44px;
    width: 44px;
    padding: 0 20px;
  }

  #schBtn::before {
    content: "search";
    color: white;
  }

  #clrBtn::before {
    content: "clear";
    color: white;
  }

  table#tblSch img,
  table#clrBtn img {
    display: none;
  }

  /*Search Input Re-purposed as Menu Button******/
  .mnTbl > tbody:nth-child(1) > tr:nth-child(1):not(:focus-within) #tblSch input {
    position: absolute;
    top: -105px;
    left: 0;
    height: 95px;
    width: 0px;
    padding-left: 100px;
    transition: height 300ms step-end, width 300ms step-end, top 300ms step-end, left 300ms step-end, padding 300ms step-end;
    cursor: pointer;
  }

  /* Toolbar Buttons ***************************/
  table.ob,
  table.ob tbody {
    display: block;
    height: 105px;
    width: calc(100vw - 468px);
  }

  table.ob tr {
    display: flex;
    justify-content: right;
    align-items: center;
    height: 100%;
  }

  table.ob td {
    display: block;
    flex-basis: 20%;
    min-width: 110px;
    text-align: center;
    position: relative;
    min-height: 80%;
  }

  table.ob td.dv,
  table.ob td:empty,
  table.ob div.sMod {
    display: none;
  }

  table.ob td > * {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }

  table.ob div.sch {
    padding: 0;
    margin: 0;
  }

  table.ob a {
    display: block;
    height: 100%;
    width: 100%;
    opacity: 0;
  }

  table.ob td::before {
    padding-top: 15%;
  }

  /*Individual toolbar icons********************/
  table.ob td:nth-child(1)::before {
    content: "people_outline";
  }

  table.ob td:nth-child(4)::before {
    content: "tune";
  }

  table.ob td:nth-child(6)::before {
    content: "help_outline";
  }

  table.ob td:nth-child(8)::before {
    content: "exit_to_app";
  }

  /***********************************************************************************************/
  /* MID-TABLE - SLIDE NAV ***********************************************************************/
  table.mnTbl td.nvtp,
  table.mnTbl td.attchnvtp {
    display: block;
    position: absolute;
    width: 0px;
    height: 0px;
    top: 0;
    left: 0;
    z-index: 20;
  }

  table.mnTbl td.nvtp > table,
  table.mnTbl td.attchnvtp > table {
    display: block;
    position: absolute;
    width: 50vw;
    left: 0;
    top: 0;
    transform: translateX(-100%);
    transition: transform 300ms ease;
  }

  table.mnTbl td.nvtp > table > tbody,
  table.mnTbl td.attchnvtp > table > tbody {
    background-color: #0972C5;
    height: 100vh;
  }

  .mnTbl > tbody:nth-child(1) > tr:nth-child(1):focus-within ~ tr:nth-child(2) .nvtp > table,
  .mnTbl > tbody:nth-child(1) > tr:nth-child(1):focus-within ~ tr:nth-child(2) .attchnvtp > table,
  table.mnTbl td.nvtp:focus-within > table,
  table.mnTbl td.attchnvtp:focus-within > table {
    transform: translateX(0);
  }

  table.mnTbl td.nvtp > table,
  table.mnTbl td.attchnvtp > table {
    height: 100vh;
    width: fit-content;
    width: -moz-fit-content;
    overflow-y: scroll;
  }

  table.mnTbl td.nvtp > table > tbody .wh100 {
    height: -moz-fit-content;
    height: fit-content;
  }

  .nvwh100 > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
    padding-bottom: 40px;
  }

  /* Seperator *********************************/
  table.mnTbl td.spc {
    display: block;
    width: 0px;
    height: 0px;
    overflow: hidden;
  }

  /* Sections **********************************/
  table.pnt {
    border: none;
  }

  table.pnt td {
    width: 350px;
  }

  table.pnt td > img {
    display: none;
  }

  table.pnt td > a.tp,
  table.pnt td > a.pn,
  table.pnt td > a.lb,
  table.pnt td > a.db,
  table.pnt td > a.pn:hover {
    padding: 25px 10px;
    background-color: transparent;
    border-bottom: 1px solid rgba(255,255,255,0.7);
    font-size: xx-large;
    font-size: 45px;
    font-weight: normal;
    color: white;
  }

  table.pnt td > a.pn.s,
  table.pnt td > a.pn.s:hover {
    background-color: rgba(100,100,100,0.6);
    font-weight: bold;
    color: white;
  }

  table.pnt td > a > img {
    display: none;
  }

  table.pnt td > a::before {
    color: white;
    font-size: 45px;
    vertical-align: -5px;
  }

  #lnkNavMail::before {
    content: "email";
    padding: 0 1px;
    font-weight: normal;
  }

  #lnkNavCal::before {
    content: "event";
    padding: 0 1px;
  }

  #lnkNavContact::before {
    content: "contact_mail";
  }

  .pnt > tbody:nth-child(2) > tr:nth-child(3) td > a {
    border-bottom-width: 6px;
  }

  .pnt > tbody:nth-child(2) > tr:nth-child(4) {
    display: none;
  }

  /* Borders */
  table.sntls {
    border-top: solid white 1px;
  }

  /* Folders ***********************************/
  table.snt td.fld a,
  table.sncts td.fld a,
  table.snfmb a {
    color: white;
    font-size: xx-large;
    font-weight: normal;
    display: inline-block;
    width: 330px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  table.snt td.fld.bld a,
  table.sncts td.fld.bld a {
    width: 280px;
  }

  table.snt td.fld span.unrd,
  table.sncts td.fld span.unrd,
  table.snt td.fld span.itm,
  table.sncts td.fld span.itm {
    color: white;
    font-size: large;
    vertical-align: 12px;
    text-align: right;
    width: 50px;
    display: inline-block;
  }

  table.snt td.fld.sl,
  table.sncts td.fld.sl {
    background-color: rgba(100,100,100,0.6);
  }

  table.snt td.fld.sl a,
  table.sncts td.fld.sl a {
    font-weight: bold;
  }

  table.snt td.fld,
  table.sncts td.fld {
    padding: 15px 10px;
  }

  table.snt td.fld a img,
  table.sncts td.fld a img {
    display: none;
  }

  table.snt .fld a::before,
  table.snfmb a::before,
  table.sncts .fld a::before {
    color: white;
    font-size: xx-large;
    vertical-align: -4px;
    padding-right: 10px;
  }

  /* Mail Folders ******************************/
  table.snt:nth-child(1) .fld a[title*="Inbox"]::before,
  table.snt:nth-child(1) .fld a[title*="inbox"]::before,
  table.snt:nth-child(1) .fld a[title*="Arriv"]::before,
  table.snt:nth-child(1) .fld a[title*="arriv"]::before,
  table.snt:nth-child(1) .fld a[title*="ception"]::before {
    content: "inbox";
  }

  table.snt:nth-child(1) .fld a[title*="Draft"]::before,
  table.snt:nth-child(1) .fld a[title*="draft"]::before,
  table.snt:nth-child(1) .fld a[title*="Brouillon"]::before,
  table.snt:nth-child(1) .fld a[title*="brouillon"]::before {
    content: "create";
  }

  table.snt:nth-child(1) .fld a[title*="Junk"]::before,
  table.snt:nth-child(1) .fld a[title*="junk"]::before,
  table.snt:nth-child(1) .fld a[title*="Spam"]::before,
  table.snt:nth-child(1) .fld a[title*="spam"]::before,
  table.snt:nth-child(1) .fld a[title*="Pouriel"]::before,
  table.snt:nth-child(1) .fld a[title*="pouriel"]::before {
    content: "report";
  }

  table.snt:nth-child(1) .fld a[title*="Delete"]::before,
  table.snt:nth-child(1) .fld a[title*="delete"]::before,
  table.snt:nth-child(1) .fld a[title*="Trash"]::before,
  table.snt:nth-child(1) .fld a[title*="trash"]::before,
  table.snt:nth-child(1) .fld a[title*="Suprim"]::before,
  table.snt:nth-child(1) .fld a[title*="suprim"]::before,
  table.snt:nth-child(1) .fld a[title*="Corbeille"]::before,
  table.snt:nth-child(1) .fld a[title*="corbeille"]::before {
    content: "delete";
  }

  table.snt:nth-child(1) .fld a[title*="Sent"]::before,
  table.snt:nth-child(1) .fld a[title*="sent"]::before,
  table.snt:nth-child(1) .fld a[title*="Outbox"]::before,
  table.snt:nth-child(1) .fld a[title*="outbox"]::before,
  table.snt:nth-child(1) .fld a[title*="Envoy"]::before,
  table.snt:nth-child(1) .fld a[title*="envoy"]::before,
  table.snt:nth-child(1) .fld a[title*="part"]::before,
  table.snt:nth-child(1) .fld a[title*="part"]::before {
    content: "markunread_mailbox";
  }

  table.snt .fld a::before {
    content: "folder";
  }

  /* All Folders Drop-Down *********************/
  table.brwst {
    border-top: solid white 1px;
    border-bottom: solid white 1px;
  }

  span.brws {
    text-align: left;
  }

  #selbrfld {
    font-size: x-large;
    height: 50px;
    width: 250px;
    margin: 10px;
  }

  #lnkBrwsAllFldrs {
    display: block;
    background-color: transparent;
    color: white;
    font-size: x-large;
    width: 330px;
    overflow: hidden;
    white-space: pre-line;
    word-wrap: normal;
    text-align: left;
  }

  #lnkBrwsAllFldrs img {
    display: none;
  }

  #lnkBrwsAllFldrs::after {
    content: "expand_more";
    color: white;
    height: 10px;
    width: 10px;
    font-size: 50px;
    vertical-align: -17px;
  }

  #lnkGotoFldr {
    display: inline-block;
    height: 50px;
    vertical-align: -14px;
    padding: 0 8px;
  }

  #lnkGotoFldr img {
    display: none;
  }

  #lnkGotoFldr::before {
    content: "play_arrow";
    color: white;
  }

  /* Calendar **********************************/
  /*Includes New Appointments*/
  table.csnv,
  table.mnTbl td.g {
    background-color:transparent;
  }

  table.mnTbl td.g {
    color: white;
    font-size: large;
  }

  table.dpht,
  table.dphst,
  table.dp,
  table.dpst {
    margin: 0 10px;
    width: calc(100% - 20px);
  }

  /*Header*/
  table.dpht,
  table.dphst {
    margin: 10px;
    table-layout: fixed;
  }

  table.dpht tr.c,
  table.dphst tr.c,
  table.dpht tr,
  table.dphst tr {
    background-color: transparent;
    color: white;
    font-size: xx-large;
  }

  table.dpht td,
  table.dphst td {
    width: 60px;
    height: 40px;
  }

  table.dpht tr td.m,
  table.dphst tr td.m {
    width: 180px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  table.dpht td a,
  table.dphst td a {
    display: block;
    width: 100%;
    height: 100%;
  }

  table.dpht td a img,
  table.dphst td a img {
    display: none;
  }

  table.dpht td a::before,
  table.dphst td a::before {
    color: white;
    font-size: xx-large;
    padding-top: 5px;
  }

  table.dpht td:nth-child(1) a::before,
  table.dphst td:nth-child(1) a::before {
    content: "navigate_before";
  }

  table.dpht td:nth-child(3) a::before,
  table.dphst td:nth-child(3) a::before {
    content: "navigate_next";
  }

  /*Dates*/
  table.dp,
  table.dpst {
    margin-bottom: 20px;
  }

  table.dp td,
  table.dpst td,
  table.dpst td.por {
    text-align: left;
    border-left: 1px solid rgba(255,255,255,0.2);
    border-bottom: 1px solid rgba(255,255,255,0.2);
  }

  table.dp tr td:last-child,
  table.dpst tr td:last-child,
  table.dpst td.por:last-child {
    border-right: 1px solid rgba(255,255,255,0.2)
  }

  table.dp tr:first-of-type td,
  table.dpst tr:first-of-type td {
    color: white;
    text-align: center;
    border-left: none;
    border-right: none;
  }

  table.dp td a,
  table.dpst td a {
    color: white;
    height: 30px;
    font-size: medium;
  }

  table.dpst td.pst {
    background-color: transparent;
  }

  table.dpst td.grt {
    background-color: rgba(200,200,200,0.3);
  }

  table.dpst td.por {
    border-top: none;
    background-color: #655FC1;
  }

  table.dpst td.tb {
    border-top: 1px solid #f90;
    border-bottom: 1px solid #f90;
  }

  table.dpst td.sd {
    border-left: 1px solid #f90;
  }

  table.dpst td.r {
    border-right: 1px solid #f90;
  }

  table.dp td.pn a,
  table.dpst td.pn a {
    color: rgba(255,255,255,0.4);
  }

  table.dp td.sd,
  table.dpst td.sd {
    background-color: rgba(100,100,100,0.6);
  }

  table.dp td.td,
  table.dpst td.td {
    border-color: rgba(255,255,255,0.2);
    border-top: none;
    text-decoration: underline double white;
  }

  table.dpst td.td {
    background-image: none;
  }

  /*List*/
  table.snt td.clhdr {
    border-top: 1px solid white;
    color: white;
    font-size: x-large;
    font-weight: normal;
    padding-left: 10px;
  }

  table.dp + table.snt .fld a::before {
    content: "date_range";
  }

  /* Contacts **********************************/
  table.sncts .fld a::before {
    content: "people";
  }

  /* Managed Folder ****************************/
  table.snfmb a,
  table.snfmb a:link,
  table.snfmb a:visited,
  table.snfmb a:hover,
  table.snfmb a:active {
    color: white;
    text-decoration: none;
  }

  table.snfmb a img {
    display: none;
  }

  table.snfmb a::before {
    content: "settings";
  }

  /* Address Book ******************************/
  table.snt td.absn {
    color: white;
    font-size: medium;
    padding: 8px;
  }

  /* Options ***********************************/
  td.nvtp table.on {
    background-color: transparent;
    table-layout: fixed;
  }

  td.nvtp table.on td {
    width: 339px;
    padding: 15px 10px;
  }

  td.nvtp table.on td a {
    display: block;
    width: calc(100% - 20px);
  }

  td.nvtp table.on td.s,
  td.nvtp table.on td a {
    color: white;
    font-size: xx-large;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
  }

  table.on td.s {
    background-color: rgba(100,100,100,0.6);
    border: none;
  }

  table.on tr:last-child {
    display: none;
  }

  /* New Message *******************************/
  table.nvwh100 td.lt {
    border: none;
  }

  table.rcntRcpt {
    background-color: transparent;
  }

  table.rcntRcpt .hd {
    color: white;
    font-size: large;
  }

  table.rcntRcpt td.sl select {
    width: 100%;
    font-size: medium;
  }

  table.rcntRcpt td.btn {
    display: block;
    width: 100%;
    height: 60px;
    box-sizing: border-box;
    margin: 20px 0;
    background-color: transparent;
    border: 2px solid white;
  }

  table.rcntRcpt td.btn a {
    display: block;
    height: 100%;
    box-sizing: border-box;
    padding-top: 10px;
    color: white;
    font-size: x-large;
    font-weight: bold;
  }

  table.rcntRcpt td.drp {
    color: white;
    font-size: medium;
  }

  /* Attachment ********************************/
  table.nvwh100 td.h100 div {
    height: -moz-fit-content;
    height: fit-content;
  }

  table.attchfrm {
    background-color: transparent;
    color: white;
    font-size: x-large;
  }

  table.attchfrm .txt {
    font-size: 20px;
  }

  table.attchfrm #attach {
    width: 320px;
    height: 60px;
    background-color: transparent;
    border: none;
    color: white;
  }

  table.attchfrm td input.btn {
    background-color: transparent;
    border: 2px solid white;
    color: white;
    font-size: x-large;
    width: 60%;
    height: 60px;
    margin-top: 20px;
  }

  /*New Meetings********************************/
  .csnv > tbody:nth-child(2) > tr:nth-child(5) > td:nth-child(1) {
    color: white;
    font-size: x-large;
  }

  #seldur {
    height: 50px;
  }

  #chkwh {
    display: inline-block;
    transform: scale(3);
    transform-origin: top left;
    vertical-align: -35px;
  }

  #chkwh + label {
    display: inline-block;
    margin-left: 50px;
  }

  #btnupd {
    height: 50px;
    width: 60%;
    margin-top: 20px;
    
    background-color: transparent;
    border: 2px solid white;
    color: white;
    font-size: x-large;
  }

  table.apptsum {
    background-color: transparent;
    color: white;
    font-size: x-large;
  }

  table.apptsum td.ashd {
    color: white;
    font-weight: bold;
  }

  /***********************************************************************************************/
  /* MID-TABLE - CONTENT *************************************************************************/
 
