  .edtMsg span#spnAtmt a#lnkAtmt,
  table.edtMsg td.lt a,
  .edtMsg span.rwRO a {
    color: #0972C5;
  }
  
  .edtMsg img.sI {
    height: 24px;
    width: auto;
  }
  
  /*message body*/
  table.bdyt td.bdy textarea {
    resize: none;
    height: 1200px;
    margin-bottom: 30px;
    border-color: gray;
    font-size: x-large;
    width: calc(100% - 10px);
  }
  
  @media (orientation: landscape) {
    table.bdyt td.bdy textarea:focus {
      display: block;
      position: absolute;
      top: 110px;
      left: 0;
      height: calc(100vh - 112px);
      width: 100vw;
      border: none;
    }
  }
