@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
    body {
        background: white !important;
    }
    .header-wrap--home, .site-wrapper--home {
        border-top-width: 0px !important;
        background: none !important;
    }
    #search_form_homepage, #search_form {
        box-shadow: 0px 1.5px 2px .5px lightgray !important;
        border: 3px gray !important;
        background-color: white !important;
        border-radius: 1.5px !important;
        transition: all 200ms !important;
        font-size: 100% !important;
    }
    .tag-home__item {
        font-size: 80% !important;
    }
    .acp {
        font-size: 130% !important;
    }
    #search_form_homepage:hover, #search_form:hover, .search__autocomplete {
        box-shadow: 0px 3px 8px .5px lightgray !important;
        border: none !important;
    }    
    #search_button_homepage, #search_form_input_clear {
        opacity: 0 !important;
        transition: all 500ms !important;
    }
    #search_button_homepage:hover, #search_form_input_clear:hover, #search_button {
        opacity: 1 !important;
        background-color: white !important;
        color: #7AA9F7 !important;
    }
    .header-wrap {
        border-top-width: 0px !important;
        background-color: #FAFAFA !important;
        box-shadow: 0px 1px 0px #EBEBEB !important;
    }
    .zcm__link.is-active {
        color: #4285F4 !important;
        border-bottom-color: #4285F4 !important;
        font-weight: bold !important;
    }
    .zcm__link.is-highlighted, .zcm__link:focus{
       	color: #4285F4 !important;
    }
    .module, .tile {
        border: 1px solid rgb(235, 235, 235) !important;
        box-shadow: 0px 2px 1px 0px rgba(0, 0, 0, .15) !important;
        border-radius: 2px !important;
    }
    .zcm__item {
        padding: 4px !important;
    }
    #duckbar {
        padding-bottom: 5px !important;
    }
    .result__menu {
        display: inline !important;
    }
    .zcm__sep--h {
        display: none !important;
    }
