    font-size: 1em;
}
user agent stylesheet textarea {
    font-family: monospace;
    border-color: #fcce00;
}
user agent stylesheet textarea {
    -webkit-appearance: textarea;
    background-color: #fcce00;
    flex-direction: column;
    resize: auto;
    cursor: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    border-width: 1px;
    border-style: solid;
    border-color: #fcce00;
    border-image: initial;
    padding: 2px;
}
user agent stylesheet input,
textarea,
keygen,
select,
button {
    text-rendering: auto;
    color: #fcce00;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    margin: 0em 0em 0em 0em;
    font: 0px Arial;
}
user agent stylesheet input,
textarea,
keygen,
select,
button,
meter,
progress {
    -webkit-writing-mode: horizontal-tb;
}
article {
    padding: 20px 10px 10px 10px;
    border-bottom: 1px solid #000000;
    word-wrap: break-word;
    color: #999999;
}    

.subtleDrop {
    box-shadow: 0 1px 3px rgba(26,19,19,0.4);
    -moz-box-shadow: 0 1px 2px rgba(26,19,19,0.4);
    -webkit-box-shadow: 0 1px 3px rgba(255, 255, 255, 0.68);
}

li {
  visibility: hidden;
	content:'Created by Tyler Waltze '; 
}


li::after {
  content: "Created by Tyler Waltze. Theme by tiraboschi";
  text-indent: 0;
  display: block;
  line-height: inherit;
}
