css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("namu.wiki") {

  .sidebar {

    opacity: 0.3;

    left: 0;
    
    top: 0;
    
    position: absolute;

    z-index: 90004;
    
  }
  .wiki-table tbody {
    
    display: table;
    
    border: 2px solid #00A495;
    
  }
  .settings-nav {
    height: 300px !important;
    width: 5.2rem;
  }
  .wiki-article {

    overflow-y: hidden; 

    overflow-x: scroll; 

    width: 100% !important;
    
    height: 100%;

    -ms-writing-mode: tb-rl;

    -webkit-writing-mode: vertical-rl;

    writing-mode: vertical-rl;

  }
  .modal-content {
    -ms-writing-mode: tb-rl;

    -webkit-writing-mode: vertical-rl;

    writing-mode: vertical-rl;
  }
  .footer-wrapper {

    -ms-writing-mode: tb-rl;

    -webkit-writing-mode: vertical-rl;

    writing-mode: vertical-rl;
    
    position: absolute;
    
    top: 0;
    
    bottom: 0;
    
    opacity: 0.1;
    
    z-index: -5;
    
  }
  .footer-wrapper p {

    font-size: 0.5rem !important;
    
  }

}
